UPDATE `creature_text_locale` SET `CreatureID`='10162', `GroupID`='0', `ID`='0', `Locale`='esES', `text`='Excelente... es como si esos entrometidos insectos vinieran justo a tiempo para servir de comida a mi legión. ¡Bienvenidos, mortales!' WHERE (`CreatureID`='10162') AND (`GroupID`='0') AND (`ID`='0');
