UPDATE `creature_text_locale` SET `CreatureID`='10162', `GroupID`='6', `ID`='0', `Locale`='esES', `text`='¡Usa el aliento helado, imbécil!' WHERE (`CreatureID`='10162') AND (`GroupID`='6') AND (`ID`='0');
