UPDATE `creature_text_locale` SET `CreatureID`='10162', `GroupID`='8', `ID`='0', `Locale`='esES', `text`='El Jefe de Guerra será rápido con vosotros, mortales. ¡Preparaos!' WHERE (`CreatureID`='10162') AND (`GroupID`='8') AND (`ID`='0');
