UPDATE `creature_text_locale` SET `CreatureID`='10162', `GroupID`='10', `ID`='0', `Locale`='esES', `text`='Esta victoria durará poco. ¡Los días divididos entre la Horda y la Alianza están llegando a su fin! La próxima vez que nos encontremos será la última.' WHERE (`CreatureID`='10162') AND (`GroupID`='10') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='10162', `GroupID`='11', `ID`='0', `Locale`='esES', `text`='¡Los mortales han caído! ¡La nueva era de la legión de Roca Negra está cerca!' WHERE (`CreatureID`='10162') AND (`GroupID`='11') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='10162', `GroupID`='12', `ID`='0', `Locale`='esES', `text`='En este mundo dónde el tiempo es tu enemigo, es mi mayor aliado. Este gran juego de la vida que crees jugar, en realidad juega contigo. Y yo digo...' WHERE (`CreatureID`='10162') AND (`GroupID`='12') AND (`ID`='0');
