UPDATE `creature_text_locale` SET `CreatureID`='13020', `GroupID`='0', `ID`='0', `Locale`='esES', `text`='¡Demasiado tarde, amigos! Ahora estoy poseído por la corrupción de Nefarius... No puedo... controlarme.' WHERE (`CreatureID`='13020') AND (`GroupID`='0') AND (`ID`='0');
