-- Lord Victor Nefarius --
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='0', `id`='0', `text_loc6`='Excelente... es como si esos entrometidos insectos vinieran justo a tiempo para servir de comida a mi legión. ¡Bienvenidos, mortales!' WHERE (`entry`='10162') AND (`groupid`='0') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='1', `id`='0', `text_loc6`='No dejéis ni una sola gota de su sangre en el suelo, hijos míos. ¡Devorad sus almas!' WHERE (`entry`='10162') AND (`groupid`='1') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='2', `id`='0', `text_loc6`='Idiotassss... ¡Matad al del vestido!' WHERE (`entry`='10162') AND (`groupid`='2') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='3', `id`='0', `text_loc6`='¡Inconcebible!' WHERE (`entry`='10162') AND (`groupid`='3') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='4', `id`='0', `text_loc6`='Vuestros esfuerzos resultarán inútiles ¡Nada se interpondrá en nuestro camino!' WHERE (`entry`='10162') AND (`groupid`='4') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='5', `id`='0', `text_loc6`='¡No me obligues, hijo! O usaré tus pieles para forrar mis botas.' WHERE (`entry`='10162') AND (`groupid`='5') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='6', `id`='0', `text_loc6`='¡Usa el aliento helado, imbécil!' WHERE (`entry`='10162') AND (`groupid`='6') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='7', `id`='0', `text_loc6`='¡¡NO PUEDE SER!! Rend, encárgate de esos insectos.' WHERE (`entry`='10162') AND (`groupid`='7') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='8', `id`='0', `text_loc6`='El Jefe de Guerra será rápido con vosotros, mortales. ¡Preparaos!' WHERE (`entry`='10162') AND (`groupid`='8') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='9', `id`='0', `text_loc6`='¡Prueba mi poder!' WHERE (`entry`='10162') AND (`groupid`='9') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='10', `id`='0', `text_loc6`='Esta victoria durará poco. ¡Los días divididos entre la Horda y la Alianza están llegando a su fin! La próxima vez que nos encontremos será la última.' WHERE (`entry`='10162') AND (`groupid`='10') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='11', `id`='0', `text_loc6`='¡Los mortales han caído! ¡La nueva era de la legión de Roca Negra está cerca!' WHERE (`entry`='10162') AND (`groupid`='11') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='12', `id`='0', `text_loc6`='En este mundo dónde el tiempo es tu enemigo, es mi mayor aliado. Este gran juego de la vida que crees jugar, en realidad juega contigo. Y yo digo...' WHERE (`entry`='10162') AND (`groupid`='12') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='13', `id`='0', `text_loc6`='¡Que comiencen los juegos!' WHERE (`entry`='10162') AND (`groupid`='13') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='10162', `groupid`='14', `id`='0', `text_loc6`='Ah... Los héroes. Sois obstinados, ¿verdad? Vuestro aliado ha intentado medir nuestros poderes y ha pagado el precio por ello. Ahora él me servirá... y os descuartizará. ¡Levántate dragoncito rojo... y destrúyelos!' WHERE (`entry`='10162') AND (`groupid`='14') AND (`id`='0');

-- Grethok el Controlador --
UPDATE `locales_creature_text` SET `entry`='12557', `groupid`='0', `id`='0', `text_loc6`='¡Los invasores han penetrado en El Criadero! ¡Activad la alarma! ¡Hay que proteger los huevos a toda costa!' WHERE (`entry`='12557') AND (`groupid`='0') AND (`id`='0');

