UPDATE `locales_creature_text` SET `entry`='12435', `groupid`='0', `id`='0', `text_loc6`='¡Pagaréis por obligarme a hacer esto!' WHERE (`entry`='12435') AND (`groupid`='0') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='12435', `groupid`='1', `id`='0', `text_loc6`='¡Locos! ¡Esos huevos son más valiosos de lo que creéis!' WHERE (`entry`='12435') AND (`groupid`='1') AND (`id`='0');
