UPDATE `locales_creature_text` SET `entry`='12435', `groupid`='3', `id`='0', `text_loc6`='¡Si yo caigo en el abismo, vosotros mortales vendréis todos conmigo!' WHERE (`entry`='12435') AND (`groupid`='3') AND (`id`='0');

-- Vaelastrasz el Corrupto --
UPDATE `locales_creature_text` SET `entry`='13020', `groupid`='0', `id`='0', `text_loc6`='¡Demasiado tarde, amigos! Ahora estoy poseído por la corrupción de Nefarius... No puedo... controlarme.' WHERE (`entry`='13020') AND (`groupid`='0') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='13020', `groupid`='1', `id`='0', `text_loc6`='Os lo ruego, mortales. ¡ESCAPAD! ¡Escapad antes de que pierda todo el control! ¡Las llamaradas negras se extienden en mi corazón! ¡DEBO liberarlas!' WHERE (`entry`='13020') AND (`groupid`='1') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='13020', `groupid`='2', `id`='0', `text_loc6`='¡FUEGO! ¡MUERTE! ¡DESTRUCCIÓN! Postraos, mortales, ante la ira del Señor... ¡NO, DEBO luchar contra esto! ¡Alexstrasza ayúdame, tengo que combatirlo!' WHERE (`entry`='13020') AND (`groupid`='2') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='13020', `groupid`='3', `id`='0', `text_loc6`='¡El odio de Nefarius me ha hecho más fuerte que nunca! ¡Deberíais haber escapado mientras podíais, mortales! ¡La furia de Roca Negra corre por mis venas!' WHERE (`entry`='13020') AND (`groupid`='3') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='13020', `groupid`='4', `id`='0', `text_loc6`='¡Perdóname, $N! ¡Tu muerte sólo aumenta mi fracaso!' WHERE (`entry`='13020') AND (`groupid`='4') AND (`id`='0');

