UPDATE `locales_creature_text` SET `entry`='12017', `groupid`='1', `id`='0', `text_loc6`='¡Inteligente, mortales! ¡Pero no me atraeréis tan lejos de mi santuario tan fácilmente!' WHERE (`entry`='12017') AND (`groupid`='1') AND (`id`='0');

