-- Nefarian --
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='0', `id`='0', `text_loc6`='Bien hecho, mis esbirros. El coraje de los mortales empieza a mermar. ¡Veamos ahora cómo se enfrentan al verdadero Señor de la Cumbre de Roca Negra!' WHERE (`entry`='11583') AND (`groupid`='0') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='0', `id`='1', `text_loc6`='¡Basta! Ahora, vermin, sentirás la fuerza de mi legado, la mismísima furia de la tierra.' WHERE (`entry`='11583') AND (`groupid`='0') AND (`id`='1');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='0', `id`='2', `text_loc6`='¡ARDED! ¡Desdichados! ¡ARDED!' WHERE (`entry`='11583') AND (`groupid`='0') AND (`id`='2');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='1', `id`='0', `text_loc6`='¡Imposible! ¡Erguíos, mis esbirros! ¡Servid a vuestro maestro una vez más!' WHERE (`entry`='11583') AND (`groupid`='1') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='2', `id`='0', `text_loc6`='¡Eres un inútil, $N! ¡Tus amigos pronto se reunirán contigo!' WHERE (`entry`='11583') AND (`groupid`='2') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='3', `id`='0', `text_loc6`='¡¡No puede ser cierto!! ¡Yo soy aquí el maestro! ¡Vosotros, mortales, no sois nada para mi raza! ¿Habéis entendido? ¡Nada!' WHERE (`entry`='11583') AND (`groupid`='3') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='4', `id`='0', `text_loc6`='¿Magos también? Deberíais tener más cuidado cuando jugáis con la magia...' WHERE (`entry`='11583') AND (`groupid`='4') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='5', `id`='0', `text_loc6`='¡Guerreros, sé que podéis golpear más fuerte que eso! ¡Venga!' WHERE (`entry`='11583') AND (`groupid`='5') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='6', `id`='0', `text_loc6`='Los druidas y sus cambios de forma. ¡Permitidme verlo en acción!' WHERE (`entry`='11583') AND (`groupid`='6') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='7', `id`='0', `text_loc6`='¡Sacerdotes! ¡Si sois capaces de mantener la curación a este ritmo, puede que podamos hacer de esto algo más interesante!' WHERE (`entry`='11583') AND (`groupid`='7') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='8', `id`='0', `text_loc6`='Paladines... He oído que tenéis mucha vida. Demuéstramelo.' WHERE (`entry`='11583') AND (`groupid`='8') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='9', `id`='0', `text_loc6`='¡Chamanes! ¡Mostradme los tótems que podéis invocar!' WHERE (`entry`='11583') AND (`groupid`='9') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='10', `id`='0', `text_loc6`='No deberíais estar jugando con magia que no entendéis, brujos. ¿Véis lo que pasa?' WHERE (`entry`='11583') AND (`groupid`='10') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='11', `id`='0', `text_loc6`='¡Los cazadores y sus molestas cerbatanas!' WHERE (`entry`='11583') AND (`groupid`='11') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='12', `id`='0', `text_loc6`='¿Pícaros? ¡Dejad de esconderos y enfrentaos a mi!' WHERE (`entry`='11583') AND (`groupid`='12') AND (`id`='0');
UPDATE `locales_creature_text` SET `entry`='11583', `groupid`='13', `id`='0', `text_loc6`='¡Caballeros de la Muerte... venid aquí!' WHERE (`entry`='11583') AND (`groupid`='13') AND (`id`='0');