UPDATE `creature_text_locale` SET `CreatureID`='28070', `GroupID`='31', `ID`='0', `Locale`='esES', `Text`='Usaré la forja para hacer terráneos que harán guardia... ¡Pero nuestro gran desafío continúa: encontrar y detener a Loken!' WHERE (`CreatureID`='28070') AND (`GroupID`='31') AND (`ID`='0');
