UPDATE `creature_text_locale` SET `CreatureID`='37221', `GroupID`='3', `ID`='0', `Locale`='esES', `Text`='¡Atrás! ¡Si tocáis la espada, vuestra alma quedará marcada toda la eternidad! Debo intentar ponerme en contacto con los espíritus atrapados en la Agonía de Escarcha. ¡Haceos a un lado! ¡Atrás!' WHERE (`CreatureID`='37221') AND (`GroupID`='3') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37221', `GroupID`='2', `ID`='0', `Locale`='esES', `Text`='La Agonía de Escarcha: la espada que destrozó nuestro reino.' WHERE (`CreatureID`='37221') AND (`GroupID`='2') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37221', `GroupID`='1', `ID`='0', `Locale`='esES', `Text`='¿Qué es eso? ¡Ahí delante! ¿Será…? ¡Héroes, a mí!' WHERE (`CreatureID`='37221') AND (`GroupID`='1') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37221', `GroupID`='0', `ID`='0', `Locale`='esES', `Text`='El frío de este lugar... Puedo sentir cómo se congela mi sangre.' WHERE (`CreatureID`='37221') AND (`GroupID`='0') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37221', `GroupID`='4', `ID`='0', `Locale`='esES', `Text`='¡Uther! ¡Querido Uther! Lo... lo siento.' WHERE (`CreatureID`='37221') AND (`GroupID`='4') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37221', `GroupID`='5', `ID`='0', `Locale`='esES', `Text`='¿Arthas está aquí? Tal vez pueda...' WHERE (`CreatureID`='37221') AND (`GroupID`='5') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37221', `GroupID`='6', `ID`='0', `Locale`='esES', `Text`='Pero Uther, si hay alguna esperanza de llegar a Arthas. Debo… intentarlo.' WHERE (`CreatureID`='37221') AND (`GroupID`='6') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37221', `GroupID`='7', `ID`='0', `Locale`='esES', `Text`='Dime cómo, Uther. ¿Cómo acabo con mi príncipe? Mi...' WHERE (`CreatureID`='37221') AND (`GroupID`='7') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37221', `GroupID`='8', `ID`='0', `Locale`='esES', `Text`='Tienes razón, Uther. Perdóname. No... no sé qué me ha pasado. Daremos esta información al Rey y a los caballeros que se enfrentan a la Plaga en la Ciudadela de la Corona de Hielo.' WHERE (`CreatureID`='37221') AND (`GroupID`='8') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37221', `GroupID`='9', `ID`='0', `Locale`='esES', `Text`='¿Quién afrontaría tal carga?' WHERE (`CreatureID`='37221') AND (`GroupID`='9') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37221', `GroupID`='10', `ID`='0', `Locale`='esES', `Text`='Entonces, aún hay esperanza...' WHERE (`CreatureID`='37221') AND (`GroupID`='10') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37221', `GroupID`='11', `ID`='0', `Locale`='esES', `Text`='¡No me privarás de esto, Arthas! Debo saber... Debo averiguar...' WHERE (`CreatureID`='37221') AND (`GroupID`='11') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='36955', `GroupID`='0', `ID`='0', `Locale`='esES', `Text`='Es demasiado poderoso. ¡Debemos abandonar este lugar ahora mismo! Mi magia podrá inmovilizarlo brevemente. ¡Vamos rápido, héroes!' WHERE (`CreatureID`='36955') AND (`GroupID`='0') AND (`ID`='0');
