UPDATE `creature_text_locale` SET `CreatureID`='36955', `GroupID`='2', `ID`='0', `Locale`='esES', `Text`='¡Otro muro de hielo! Evitad que los no-muertos interrumpan mis conjuros para que pueda derribar este muro.' WHERE (`CreatureID`='36955') AND (`GroupID`='2') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='36955', `GroupID`='3', `ID`='0', `Locale`='esES', `Text`='¡Está jugando con nosotros! ¡Le enseñaré lo que le pasa al hielo cuando conoce al fuego!' WHERE (`CreatureID`='36955') AND (`GroupID`='3') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='36955', `GroupID`='4', `ID`='0', `Locale`='esES', `Text`='Tus muros no podrán detenernos mucho más, monstruo. ¡Los destruiré todos!' WHERE (`CreatureID`='36955') AND (`GroupID`='4') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='36955', `GroupID`='5', `ID`='0', `Locale`='esES', `Text`='Hay una salida ahí delante. ¡VAMOS!' WHERE (`CreatureID`='36955') AND (`GroupID`='5') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='36955', `GroupID`='6', `ID`='0', `Locale`='esES', `Text`='Casi estamos… ¡No os rindáis!' WHERE (`CreatureID`='36955') AND (`GroupID`='6') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='36955', `GroupID`='7', `ID`='0', `Locale`='esES', `Text`='No... no hay salida. No tenemos más opción que luchar. Armaos de valor, héroes, pues esta es nuestra última batalla.' WHERE (`CreatureID`='36955') AND (`GroupID`='7') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='36955', `GroupID`='8', `ID`='0', `Locale`='esES', `Text`='Perdonadme, héroes. Debí escuchar a Uther. Tenía… tenía que… verlo por mí misma. Poder mirarle a los ojos una última vez. Lo siento.' WHERE (`CreatureID`='36955') AND (`GroupID`='8') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='36955', `GroupID`='9', `ID`='0', `Locale`='esES', `Text`='Ahora sabemos qué debemos hacer. Les daré estas noticias al rey Varian y al alto señor Vadín.' WHERE (`CreatureID`='36955') AND (`GroupID`='9') AND (`ID`='0');

-- Lady Sylvanas Brisaveloz --
UPDATE `creature_text_locale` SET `CreatureID`='37223', `GroupID`='0', `ID`='0', `Locale`='esES', `Text`='¡No... no puede ser! La Agonía de Escarcha está justo ante nosotros, sin vigilancia, tal y como dijo el gnomo. ¡Venid, héroes!' WHERE (`CreatureID`='37223') AND (`GroupID`='0') AND (`ID`='0');
