UPDATE `creature_text_locale` SET `CreatureID`='37223', `GroupID`='2', `ID`='0', `Locale`='esES', `Text`='No me atrevo a tocarla. ¡Atrás! ¡Atrás mientras intento comunicarme con la espada! Puede que nuestra salvación se halle dentro...' WHERE (`CreatureID`='37223') AND (`GroupID`='2') AND (`ID`='0');
