UPDATE `creature_text_locale` SET `CreatureID`='37223', `GroupID`='5', `ID`='0', `Locale`='esES', `Text`='Tiene que haber un modo...' WHERE (`CreatureID`='37223') AND (`GroupID`='5') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37223', `GroupID`='6', `ID`='0', `Locale`='esES', `Text`='¿Quién afrontaría tal carga?' WHERE (`CreatureID`='37223') AND (`GroupID`='6') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37223', `GroupID`='7', `ID`='0', `Locale`='esES', `Text`='El Trono Helado...' WHERE (`CreatureID`='37223') AND (`GroupID`='7') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37223', `GroupID`='8', `ID`='0', `Locale`='esES', `Text`='¡No escaparás de mí tan fácilmente, Arthas! ¡Obtendré mi venganza!' WHERE (`CreatureID`='37223') AND (`GroupID`='8') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37554', `GroupID`='0', `ID`='0', `Locale`='esES', `Text`='Es... demasiado poderoso. Héroes, rápido… ¡venid! ¡Debemos abandonar este lugar ahora mismo! Haré lo que pueda para inmovilizarlo mientras huimos.' WHERE (`CreatureID`='37554') AND (`GroupID`='0') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37554', `GroupID`='1', `ID`='0', `Locale`='esES', `Text`='Ninguna pared detendrá a la Reina alma en pena. Mantened a los no-muertos a raya, héroes. ¡Destruiré la barrera!' WHERE (`CreatureID`='37554') AND (`GroupID`='1') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37554', `GroupID`='2', `ID`='0', `Locale`='esES', `Text`='¡Otra barrera! Sed fuertes, campeones. Derrumbaré el muro.' WHERE (`CreatureID`='37554') AND (`GroupID`='2') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37554', `GroupID`='3', `ID`='0', `Locale`='esES', `Text`='Me canso de estos juegos, Arthas. ¡Tus muros no me detendrán!' WHERE (`CreatureID`='37554') AND (`GroupID`='3') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37554', `GroupID`='4', `ID`='0', `Locale`='esES', `Text`='¡No impedirás que escapemos, demonio! Acabad con los no-muertos mientras derribo esta barrera.' WHERE (`CreatureID`='37554') AND (`GroupID`='4') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37554', `GroupID`='5', `ID`='0', `Locale`='esES', `Text`='Hay una salida ahí delante. ¡VAMOS!' WHERE (`CreatureID`='37554') AND (`GroupID`='5') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37554', `GroupID`='6', `ID`='0', `Locale`='esES', `Text`='Casi estamos… ¡No os rindáis!' WHERE (`CreatureID`='37554') AND (`GroupID`='6') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37554', `GroupID`='7', `ID`='0', `Locale`='esES', `Text`='¡MALDICIÓN, NO HAY SALIDA! Así es cómo acaba… ¡Preparaos, héroes, hoy tendrá lugar nuestra última batalla!' WHERE (`CreatureID`='37554') AND (`GroupID`='7') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37554', `GroupID`='8', `ID`='0', `Locale`='esES', `Text`='Estamos a salvo… por ahora. Su fuerza se ha multiplicado desde nuestra última batalla. Necesitamos un ejército poderoso para destruir al Rey Exánime, mejor incluso que el que la Horda puede convocar.' WHERE (`CreatureID`='37554') AND (`GroupID`='8') AND (`ID`='0');

