-- Uther el Iluminado --
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='0', `ID`='0', `Locale`='esES', `Text`='¡JAINA! ¿Realmente eres tú?' WHERE (`CreatureID`='37225') AND (`GroupID`='0') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='1', `ID`='0', `Locale`='esES', `Text`='Jaina, no tenéis mucho tiempo. El Rey Exánime ve lo que ve la espada. Llegará en cualquier momento.' WHERE (`CreatureID`='37225') AND (`GroupID`='1') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='2', `ID`='0', `Locale`='esES', `Text`='No, niña. Arthas no está aquí. Arthas no es más que una presencia en la mente del Rey Exánime. Un presencia cada vez más débil...' WHERE (`CreatureID`='37225') AND (`GroupID`='2') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='3', `ID`='0', `Locale`='esES', `Text`='Jaina, escúchame. Debes acabar con el Rey Exánime. No puedes razonar con él. Os matará a ti y a tus aliados y os levantará como poderosos soldados de la Plaga.' WHERE (`CreatureID`='37225') AND (`GroupID`='3') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='4', `ID`='0', `Locale`='esES', `Text`='Despierta, niña. Debes acabar con el Rey Exánime en el lugar donde se fusionó con Ner\'zhul, en lo alto de la aguja, en el Trono Helado. Es el único modo.' WHERE (`CreatureID`='37225') AND (`GroupID`='4') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='5', `ID`='0', `Locale`='esES', `Text`='Hay... algo más que deberías saber sobre el Rey Exánime. Nunca se debe perder el control sobre la Plaga. Incluso si vencéis al Rey Exánime, otro deberá ocupar su lugar. Sin el dominio de su maestro, la Plaga camparía a sus anchas por el mundo, destruyendo todo ser viviente.' WHERE (`CreatureID`='37225') AND (`GroupID`='5') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='6', `ID`='0', `Locale`='esES', `Text`='Un gran sacrificio para un alma noble...' WHERE (`CreatureID`='37225') AND (`GroupID`='6') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='7', `ID`='0', `Locale`='esES', `Text`='No lo sé, Jaina. Supongo que la parte de Arthas que vive dentro del Rey Exánime es todo lo que evita que la Plaga aniquile Azeroth.' WHERE (`CreatureID`='37225') AND (`GroupID`='7') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='8', `ID`='0', `Locale`='esES', `Text`='¡No, Jaina! ARRRRRRGHHHH... Se... acerca. Debes... debes...' WHERE (`CreatureID`='37225') AND (`GroupID`='8') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='9', `ID`='0', `Locale`='esES', `Text`='Cuidado. Ya he oído antes que esa hoja maldita nos salvaría. Mira a tu alrededor y observa lo que ha brotado de la Agonía de Escarcha.' WHERE (`CreatureID`='37225') AND (`GroupID`='9') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='10', `ID`='0', `Locale`='esES', `Text`='No tenéis mucho tiempo. El Rey Exánime ve lo que ve la espada. Llegará en cualquier momento.' WHERE (`CreatureID`='37225') AND (`GroupID`='10') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='11', `ID`='0', `Locale`='esES', `Text`='No puedes derrotar al Rey Exánime. Aquí no. Estarías loca si lo intentases. Matará a los que te siguen y los levantará como poderosos soldados de la Plaga. Pero en tu caso, Sylvanas… la recompensa que tiene para ti sería peor que la última.' WHERE (`CreatureID`='37225') AND (`GroupID`='11') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='12', `ID`='0', `Locale`='esES', `Text`='Tal vez, pero debes saber que siempre debe haber un Rey Exánime. Incluso si derrotas a Arthas, otro deberá tomar su lugar. Sin el dominio del Rey Exánime, la Plaga invadirá el mundo como langostas, destruyendo todo lo que toque.' WHERE (`CreatureID`='37225') AND (`GroupID`='12') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='13', `ID`='0', `Locale`='esES', `Text`='No lo sé, Reina alma en pena. Supongo que la parte de Arthas que vive dentro del Rey Exánime es todo lo que evita que la Plaga aniquile Azeroth.' WHERE (`CreatureID`='37225') AND (`GroupID`='13') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='14', `ID`='0', `Locale`='esES', `Text`='Por tanto, la única forma de derrotar al Rey Exánime es destruirlo en el lugar donde fue creado.' WHERE (`CreatureID`='37225') AND (`GroupID`='14') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='15', `ID`='0', `Locale`='esES', `Text`='Eso es. ARRRRRRGHHHH... Se... Acerca. Tú… tú debes...' WHERE (`CreatureID`='37225') AND (`GroupID`='15') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='16', `ID`='0', `Locale`='esES', `Text`='¡Alto! ¡No llevéis esa espada más allá!' WHERE (`CreatureID`='37225') AND (`GroupID`='16') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='17', `ID`='0', `Locale`='esES', `Text`='¿Os dais cuenta de lo que habéis hecho?' WHERE (`CreatureID`='37225') AND (`GroupID`='17') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='18', `ID`='0', `Locale`='esES', `Text`='Habéis forjado esta espada de saronita, la mismísima sangre de un dios antiguo. El poder del Rey Exánime llama a esta arma.' WHERE (`CreatureID`='37225') AND (`GroupID`='18') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='19', `ID`='0', `Locale`='esES', `Text`='Cada segundo que os demoráis, Quel\'Delar bebe del mal de este lugar.' WHERE (`CreatureID`='37225') AND (`GroupID`='19') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='20', `ID`='0', `Locale`='esES', `Text`='Solo hay una manera de limpiar esta espada. Apresuraos a La Fuente del Sol y sumergid la espada en sus aguas.' WHERE (`CreatureID`='37225') AND (`GroupID`='20') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='37225', `GroupID`='21', `ID`='0', `Locale`='esES', `Text`='No puedo resistir más la llamada de la Agonía de Escarcha…' WHERE (`CreatureID`='37225') AND (`GroupID`='21') AND (`ID`='0');

