UPDATE `creature_text_locale` SET `CreatureID`='27447', `GroupID`='2', `ID`='2', `Locale`='esES', `Text`='¡%s llama a un capitán del anillo azur!' WHERE (`CreatureID`='27447') AND (`GroupID`='2') AND (`ID`='2');
