UPDATE `creature_text_locale` SET `CreatureID`='27447', `GroupID`='4', `ID`='0', `Locale`='esES', `Text`='Intrusos, vuestra victoria será efímera. Soy el comandante Varos Zancanubes. Mis dracos controlan el cielo y protegen este conducto. ¡Yo me encargaré personalmente de que El Oculus no caiga en vuestro poder!' WHERE (`CreatureID`='27447') AND (`GroupID`='4') AND (`ID`='0');

-- Urom --
UPDATE `creature_text_locale` SET `CreatureID`='27655', `GroupID`='2', `ID`='0', `Locale`='esES', `Text`='Seguís luchando. Seguís aferrándoos a principios erróneos. Si sobrevivís, me encontraréis en el anillo central.' WHERE (`CreatureID`='27655') AND (`GroupID`='2') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27655', `GroupID`='1', `ID`='0', `Locale`='esES', `Text`='Es evidente que mis mascotas han fracasado. Quizá sea necesaria otra demostración.' WHERE (`CreatureID`='27655') AND (`GroupID`='1') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27655', `GroupID`='0', `ID`='0', `Locale`='esES', `Text`='¿Qué tenemos aquí? ¿Aquellos que se atreven a desafiar al Tejehechizos? Aquellos a quienes les falta previsión y entendimiento. ¿Cómo puedo explicároslo? ¡Malygos está intentado salvar al mundo de sí mismo! ¡Bah! ¡No malgastéis mi tiempo!' WHERE (`CreatureID`='27655') AND (`GroupID`='0') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27655', `GroupID`='3', `ID`='0', `Locale`='esES', `Text`='¡Pobres necios ciegos!' WHERE (`CreatureID`='27655') AND (`GroupID`='3') AND (`ID`='0');
