UPDATE `creature_text_locale` SET `CreatureID`='27655', `GroupID`='5', `ID`='0', `Locale`='esES', `Text`='Una muestra... ¡una pequeña muestra del poder del Tejehechizos!' WHERE (`CreatureID`='27655') AND (`GroupID`='5') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27655', `GroupID`='5', `ID`='1', `Locale`='esES', `Text`='Tanta energía inestable... ¡pero vale la pena correr el riesgo de destruiros!' WHERE (`CreatureID`='27655') AND (`GroupID`='5') AND (`ID`='1');
UPDATE `creature_text_locale` SET `CreatureID`='27655', `GroupID`='6', `ID`='0', `Locale`='esES', `Text`='Todo lo que he hecho... ha sido por Azeroth.' WHERE (`CreatureID`='27655') AND (`GroupID`='6') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27655', `GroupID`='7', `ID`='0', `Locale`='esES', `Text`='¡Si pudieras entenderlo!' WHERE (`CreatureID`='27655') AND (`GroupID`='7') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27655', `GroupID`='7', `ID`='1', `Locale`='esES', `Text`='Ahora lo ves, ¿verdad?' WHERE (`CreatureID`='27655') AND (`GroupID`='7') AND (`ID`='1');
UPDATE `creature_text_locale` SET `CreatureID`='27655', `GroupID`='7', `ID`='2', `Locale`='esES', `Text`='Desafortunado, pero necesario.' WHERE (`CreatureID`='27655') AND (`GroupID`='7') AND (`ID`='2');

-- Eregos --
UPDATE `creature_text_locale` SET `CreatureID`='27656', `GroupID`='1', `ID`='0', `Locale`='esES', `Text`='¡Entrometidos descarados, estáis fuera de vuestro elemento! ¡Os enterraré!' WHERE (`CreatureID`='27656') AND (`GroupID`='1') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27656', `GroupID`='0', `ID`='0', `Locale`='esES', `Text`='¡Simplones! No podéis comprender las fuerzas que habéis puesto en marcha. ¡El conducto de líneas Ley no será interrumpido! ¡Vuestra derrota será total!' WHERE (`CreatureID`='27656') AND (`GroupID`='0') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27656', `GroupID`='2', `ID`='0', `Locale`='esES', `Text`='Tanta insolencia... tanta arrogancia... ¡deben ser CASTIGADAS!' WHERE (`CreatureID`='27656') AND (`GroupID`='2') AND (`ID`='0');
