UPDATE `creature_text_locale` SET `CreatureID`='27656', `GroupID`='3', `ID`='2', `Locale`='esES', `Text`='¡Disfruta de la caída!' WHERE (`CreatureID`='27656') AND (`GroupID`='3') AND (`ID`='2');
UPDATE `creature_text_locale` SET `CreatureID`='27656', `GroupID`='4', `ID`='0', `Locale`='esES', `Text`='Saboread esta pequeña victoria, criaturitas insensatas. Vosotros y vuestros aliados dragones habéis ganado esta batalla, pero nosotros ganaremos la guerra de El Nexo.' WHERE (`CreatureID`='27656') AND (`GroupID`='4') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27656', `GroupID`='5', `ID`='0', `Locale`='esES', `Text`='¡Mandamos sobre lo Arcano! ¡No se puede usar contra nosotros!' WHERE (`CreatureID`='27656') AND (`GroupID`='5') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27656', `GroupID`='5', `ID`='1', `Locale`='esES', `Text`='¡Es absurdo intentar extinguir tu fuego!' WHERE (`CreatureID`='27656') AND (`GroupID`='5') AND (`ID`='1');
UPDATE `creature_text_locale` SET `CreatureID`='27656', `GroupID`='5', `ID`='2', `Locale`='esES', `Text`='¡La magia de la naturaleza no os ayudará ahora!' WHERE (`CreatureID`='27656') AND (`GroupID`='5') AND (`ID`='2');

-- Draco ámbar --
UPDATE `creature_text_locale` SET `CreatureID`='27755', `GroupID`='0', `ID`='0', `Locale`='esES', `Text`='¡%s se aleja volando!' WHERE (`CreatureID`='27755') AND (`GroupID`='0') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27755', `GroupID`='1', `ID`='0', `Locale`='esES', `Text`='Te doy la bienvenida, colega. ¡Mantén la cabeza agachada y agárrate bien!' WHERE (`CreatureID`='27755') AND (`GroupID`='1') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27755', `GroupID`='2', `ID`='0', `Locale`='esES', `Text`='Usa la Lanza de choque para dañar a los enemigos. Si nos metemos en problemas, Parar tiempo congelará en su sitio a todos los enemigos, ¡entonces dales con la Lanza de choque para hacer mucho daño!' WHERE (`CreatureID`='27755') AND (`GroupID`='2') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27755', `GroupID`='3', `ID`='0', `Locale`='esES', `Text`='Ahora que tengo todo mi poder, puedo utilizar Falla temporal. Puedes usarlo para hacer que los enemigos sufran daño adicional y conseguir Cargas de choque. ¡Guárdalas y después usa la Lanza de choque para hacer un daño enorme!' WHERE (`CreatureID`='27755') AND (`GroupID`='3') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27755', `GroupID`='4', `ID`='0', `Locale`='esES', `Text`='¡Estoy malherido! ¡No podré soportarlo mucho más!' WHERE (`CreatureID`='27755') AND (`GroupID`='4') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27755', `GroupID`='5', `ID`='0', `Locale`='esES', `Text`='Quedan $3486W ensamblajes de centrifugadora. Debemos encontrar el modo de destruirlos para desactivar el escudo protector de Varos Zancanubes. Los ensamblajes deberían estar a medio camino hacia la cima de El Oculus, en el Anillo de Aceleración.' WHERE (`CreatureID`='27755') AND (`GroupID`='5') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27755', `GroupID`='6', `ID`='0', `Locale`='esES', `Text`='Queda un ensamblaje de centrifugadora. Debemos encontrar el modo de destruirlo para desactivar el escudo protector de Varos Zancanubes. El ensamblaje debería estar a medio camino hacia la cima de El Oculus, en el Anillo de Aceleración.' WHERE (`CreatureID`='27755') AND (`GroupID`='6') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27755', `GroupID`='7', `ID`='0', `Locale`='esES', `Text`='¡El escudo está desconectado, Varos es vulnerable! Al vencerle, los guardianes del anillo azur deberían dispersarse y permitirnos acceder a los anillos superiores. Varos se encuentra sobre una plataforma flotante a medio camino hacia la cima del Oculus, en el Anillo de Aceleración.' WHERE (`CreatureID`='27755') AND (`GroupID`='7') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27755', `GroupID`='8', `ID`='0', `Locale`='esES', `Text`='El Señor de la magia Urom es nuestro siguiente objetivo. Se esconde en una de las plataformas flotantes del Anillo de Transmutación, cerca de la cima del Oculus. Mucho ojo: Urom es un conjurador muy poderoso y puede que no esté solo.' WHERE (`CreatureID`='27755') AND (`GroupID`='8') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27755', `GroupID`='9', `ID`='0', `Locale`='esES', `Text`='El Guardián-Ley Eregos es lo único que se interpone entre nosotros y el control del Oculus. Él sobrevuela el Anillo de Alineación, en lo más alto del Oculus. El poder de Eregos es increíble, quédate sobre mí cuando nos enfrentemos a él y deja que luche yo.' WHERE (`CreatureID`='27755') AND (`GroupID`='9') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='27755', `GroupID`='10', `ID`='0', `Locale`='esES', `Text`='Hemos arrebatado el control del Oculus a los ejércitos de Malygos. No estás perdiendo peso, $gpequeño:pequeña;. Mis alas ansían el cielo abierto.' WHERE (`CreatureID`='27755') AND (`GroupID`='10') AND (`ID`='0');

