-- Belgaristrasz --
UPDATE `creature_text_locale` SET `CreatureID`='27658', `GroupID`='0', `ID`='0', `Locale`='esES', `Text`='Gracias por liberarnos, mortales. Tened cuidado, el Vuelo Azul ha sido alertado de vuestra presencia. Mientras hablamos, Malygos envía a Varos Zancanubes y a sus guardianes del anillo para defender El Oculus. Necesitaréis nuestra ayuda o no tendréis nada que hacer.' WHERE (`CreatureID`='27658') AND (`GroupID`='0') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='28012', `GroupID`='0', `ID`='0', `Locale`='esES', `Text`='El tramposo Señor de la magia Urom protege el tercer anillo. Aparecerá solo e indefenso, ¡pero que no te engañen las apariencias! Urom es un poderoso conjurador que dirige un grupo de criaturas fantasmales. Búscale arriba.' WHERE (`CreatureID`='28012') AND (`GroupID`='0') AND (`ID`='0');
UPDATE `creature_text_locale` SET `CreatureID`='28012', `GroupID`='1', `ID`='1', `Locale`='esES', `Text`='El mayor de tus desafíos aún está por llegar. El Guardián-Ley Eregos es un dragón Azul de inmenso poder. Lo encontrarás sobrevolando el anillo superior.' WHERE (`CreatureID`='28012') AND (`GroupID`='1') AND (`ID`='1');