UPDATE `npc_text_locale` SET `ID`='938', `Locale`='esES', `Text0_0`='Si vamos a apoderarnos de estas tierras, tenemos que crear espacios para descansar nuestros cuerpos cansados por la batalla. Coloca tu hacha junto al fuego y nárrame tus maravillosas batallas.', `Text0_1`='Si vamos a apoderarnos de estas tierras, tenemos que crear espacios para descansar nuestros cuerpos cansados por la batalla. Coloca tu hacha junto al fuego y nárrame tus maravillosas batallas.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='938') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='940', `Locale`='esES', `Text0_0`='No hay nada como pasar un día pescando en el Lago de Cristal. Arnold Leland es quien me enseñó. Suele estar en el muelle del Distrito del Canal, pescando durante todo el día. $B$BAh, y no le creas ninguna de las historias que cuenta sobre monstruos en los canales.', `Text0_1`='No hay nada como pasar un día pescando en el Lago de Cristal. Arnold Leland es quien me enseñó. Suele estar en el muelle del Distrito del Canal, pescando durante todo el día. $B$BAh, y no le creas ninguna de las historias que cuenta sobre monstruos en los canales.', `Text1_0`='Ese vago de Leland... todo lo que hace es sentarse en los muelles de la parte exterior del Distrito de los Mercaderes y pasarse todo el día pescando. Espero que, pescando durante tanto tiempo, sea realmente bueno. Si quieres aprender, ¿por qué no vas a hablar con él?', `Text1_1`='Ese vago de Leland... todo lo que hace es sentarse en los muelles de la parte exterior del Distrito de los Mercaderes y pasarse todo el día pescando. Espero que, pescando durante tanto tiempo, sea realmente bueno. Si quieres aprender, ¿por qué no vas a hablar con él?', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='940') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='941', `Locale`='esES', `Text0_0`='Lucan Cordell tiene una pequeña tienda junto al canal, cerca de las mazmorras. Seguro que te podrá enseñar las artes del encantamiento.', `Text0_1`='Lucan Cordell tiene una pequeña tienda junto al canal, cerca de las mazmorras. Seguro que te podrá enseñar las artes del encantamiento.', `Text1_0`='Ah, estás buscando a Lucan Cordell. Lleva una tienda en las afueras del Barrio de la Magia, de camino a las Mazmorras.', `Text1_1`='Ah, estás buscando a Lucan Cordell. Lleva una tienda en las afueras del Barrio de la Magia, de camino a las Mazmorras.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='941') AND (`Locale`='esES');
