UPDATE `npc_text_locale` SET `ID`='1059', `Locale`='esES', `Text0_0`='Esta es una muestra de la flora exótica, no vista en otras partes de Teldrassil.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1059') AND (`Locale`='esES');
