UPDATE `npc_text_locale` SET `ID`='1297', `Locale`='esES', `Text0_0`='El padre Cobb es a quien buscas. Está debajo del Alto de los Espíritus en la caverna de las Pozas de las Visiones.', `Text0_1`='El padre Cobb es a quien buscas. Está debajo del Alto de los Espíritus en la caverna de las Pozas de las Visiones.', `Text1_0`='A pesar de su tragedia, los no-muertos han venido hasta aquí para ayudarnos. Solo espero poder ayudarles tanto como lo hacen ellos con nosotros. Busca al padre Cobb cerca de las Pozas de las Visiones, bajo el Alto de los Espíritus.', `Text1_1`='A pesar de su tragedia, los no-muertos han venido hasta aquí para ayudarnos. Solo espero poder ayudarles tanto como lo hacen ellos con nosotros. Busca al padre Cobb cerca de las Pozas de las Visiones, bajo el Alto de los Espíritus.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1297') AND (`Locale`='esES');
