UPDATE `npc_text_locale` SET `ID`='1299', `Locale`='esES', `Text0_0`='¿Aspiras a alcanzar nuestro rango algún día? Sark Tótem de Ira puede ayudarte a conseguirlo. Está en la Sala de los Cazadores.', `Text0_1`='¿Aspiras a alcanzar nuestro rango algún día? Sark Tótem de Ira puede ayudarte a conseguirlo. Está en la Sala de los Cazadores.', `Text1_0`='Fuerte de cuerpo y mente. ¡No puede haber una combinación más poderosa! Encuentra tu destino con Sark Tótem de Ira, en el Recinto de los Cazadores.', `Text1_1`='Fuerte de cuerpo y mente. ¡No puede haber una combinación más poderosa! Encuentra tu destino con Sark Tótem de Ira, en el Recinto de los Cazadores.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1299') AND (`Locale`='esES');
