UPDATE `npc_text_locale` SET `ID`='1673', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Por favor, un momento, $N. Kindal está abajo en el campo de Tótem Siniestro intentando liberar de su cautiverio a los Dragones feéricos. Qué suerte que llegó la ayuda en el momento oportuno. Quien sabe lo que esos asquerosos Tauren les hubiesen hecho.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1673') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1674', `Locale`='esES', `Text0_0`='Saludos. Soy un guardián de la entrada. A menos que tengas las placas de Uldum integradas en tus discos, no estás $gautorizado:autorizada; para entrar en el dominio de Uldum.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1674') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1675', `Locale`='esES', `Text0_0`='Las placas de Uldum son discos sintetizados por los Creadores que permiten el acceso al dominio de Uldum. Estos discos almacenan datos específicos relacionados con las actividades de los Creadores en este lugar.$B$BFísicamente, son casi idénticos en circunferencia al conjunto de discos que posees en estos momentos. No obstante, sus marcas contienen datos directamente relacionados con Uldum, a diferencia del complejo de Uldaman.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1675') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1676', `Locale`='esES', `Text0_0`='Mi propósito es regular el acceso al dominio de Uldum en servicio de los Creadores. Permito la entrada a este dominio cuando el aspirante muestra los discos de acceso en la secuencia adecuada.$B$BTu conjunto de discos no incluye las placas de Uldum, requisito principal para entrar. No se permite el acceso a menos que las placas de Uldum estén presentes.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1676') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1677', `Locale`='esES', `Text0_0`='Numerosos conjuntos de placas de Uldum han sido sintetizados por los Creadores para un uso específico. Los Creadores controlan la distribución de dichos discos para todos los dominios de este mundo. Por su parte, Azeroth ha sido reprogramado para la aparición de los Creadores y, por esta razón, ese momento será cuando los discos del futuro entren en circulación.$B$BLos discos existentes se han distribuido ampliamente entre aquellos que disponen de la autorización debida.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1677') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1678', `Locale`='esES', `Text0_0`='Los Creadores utilizan la aparición como medio para restablecer el control sobre los mundos creados cada vez que las fuerzas, tanto externas como internas, desestabilizan la dinámica de la matriz relacionada con ellos. Dichas tácticas no deben subestimarse cuando las ejecutan los Creadores.$B$BPara obtener información adicional sobre la aparición programada para este mundo, consulta el banco de datos adecuado que contiene todas las consideraciones de planificación relacionadas con los Creadores.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1678') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1679', `Locale`='esES', `Text0_0`='Uldum es un complejo de investigación para los Creadores en sus esfuerzos constantes de mejorar la biosfera de Azeroth. La información específica relacionada con su trabajo y, en concreto, con Uldum, se restringe a aquellos individuos que poseen las placas de Uldum en sus conjuntos de discos.$B$BLos bancos de datos guardados en el dominio de Uldum han sido programados para abordar experimentos específicos y compilaciones de datos determinadas. Consúltalos para obtener información detallada.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1679') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1693', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Gracias a la Luz que estás aquí! En estos momentos aceptaría la ayuda de un ogro de dos cabezas.$B$B¡La ciudad está invadida por troggs! Los intentos por eliminarlos provocó una contaminación masiva. Pero hay esperanzas.$B$BNuestros científicos que fueron evacuados a Forjaz están trabajando en curar las enfermedades producidas por la lluvia radiactiva. Mientras tanto, si puedes ayudarme a detener la invasión de los troggs, quizás entonces podamos recuperar la ciudad.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1693') AND (`Locale`='esES');
