UPDATE `npc_text_locale` SET `ID`='1757', `Locale`='esES', `Text0_0`='Un increíble poder se puede encontrar en los espíritus o \"muisek\" de las criaturas con las que luchamos.$B$BBusca en tu interior; aprende a utilizar el poder de tu espíritu, y serás más poderoso de lo que nunca hayas imaginado.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1757') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1758', `Locale`='esES', `Text0_0`='Sul\'thraze... la primera vez que escuché esa palabra tenía más o menos tu edad. Entonces no significaba nada para mí, así como tampoco significa nada para ti. No fue sino hasta que una banda de guerreros trol procedente de Zul\'Farrak arrasó con la tierra que yo conocía... y temía.$B$BMuchos te dirán que el Acero Partido del Sufrimiento no existe. Que es un mito, un rumor desatado para asustar a los niños. Pero créeme, $r, existe.$B$B<Trenton permanece en silencio mientras recorre con la mano la herida en zigzag que cruza su rostro.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1758') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1759', `Locale`='esES', `Text0_0`='Partida en dos, formando una sola. Las hojas gemelas están ligadas a Zul\'Farrak.$B$BSul\'thraze renace cuando se encuentran las dos mitades.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1759') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1793', `Locale`='esES', `Text0_0`='Los guardias de Theramore son el grupo de soldados más aguerrido de la Alianza con base en Kalimdor. Nuestro deber es defender las leyes de la Alianza incluso lejos de nuestros hogares.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1793') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1794', `Locale`='esES', `Text0_0`='No sabemos mucho acerca del incendio de la posada Reposo Umbrío, salvo que la mujer y el hijo de James Hyal no pudieron escapar.$B$BConseguimos sacar a James, pero él no ha vuelto a ser el mismo desde entonces.$B$BTenemos muchas pistas, pero pocas pruebas. Esperamos que gracias al esfuerzo de la guardia y de la ayuda atenta de la gente de Theramore podamos desentrañar este terrible crimen.$B$BSi deseas ayudarnos en la investigación, los restos de la posada están en la frontera de los Baldíos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1794') AND (`Locale`='esES');
