UPDATE `npc_text_locale` SET `ID`='1818', `Locale`='esES', `Text0_0`='Los humanos reclamaban para sí gran parte del Marjal Revolcafango, pero el clan Quebrantarrocas, con nuestra ayuda, ha logrado hacerles retroceder.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1818') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1833', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='No me importa lo que digas, $N. Nada de eso, de ninguna manera te dejaré entrar en la Garganta de Fuego. Es imposible, Ni hablar. ¿Cuántas veces te lo tengo que decir? ¡No puedo dejar que entres allí! ¿No entiendes mi idioma? ¿Eres un orco disfrazado? N-O. ¡NO!$B$BSi te dejo entrar, entonces tendré que dejarlos entrar a todos. Muy pronto tus amigos más cercanos querrán entrar, luego tus familiares lejanos, sus amigos etc., etc. La última vez que eso ocurrió... bueno, mejor no hablar de la última vez que ocurrió.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1833') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1834', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Has perdido la cabeza? ¿Sabes qué clase de bestia vive en ese lugar? Lo peor que puede pasar es que tengamos que recoger tus restos con una pala. Hemos perdido a excelentes enanos en las garras de aquellos terribles monstruos.$B$BAsí que la respuesta es no. Si estás $gdecidido:decidida; a entrar en la Garganta de Fuego, tendrás que ir por otro camino.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1834') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1835', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='No debería contarte esto, pero si puedes vencer a todos los bichos y bestias que hay en las Tierras Inhóspitas, entonces tal vez puedas llegar hasta la Garganta de Fuego.$B$BSí, las Tierras Inhóspitas. Ahí hay un camino alternativo que los orcos construyeron cerca de Kargath. ¡Esto es solo para que veas con qué clase de tontos estamos lidiando! ¿Una puerta trasera al infierno? ¿En qué estarían pensando?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1835') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1836', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Invulnerable?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1836') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1837', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Eres idiota si no haces caso a mis advertencias. Nadie es invulnerable. Serías $gsensato:sensata; si recordaras mi consejo.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1837') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1838', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Mira. Ya que te crees $gun héroe:una heroína;, si eres capaz de cruzar las Tierras Inhóspitas hasta la Garganta de Fuego y traerme algo como prueba de tus heroicidades, veré si puedo conseguirte una llave.$B$BEn la parte sudeste de la Garganta de Fuego hay una mujer tan exigente como yo, Margol la Furibunda. Quizás deberías indagar un poco.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1838') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1853', `Locale`='esES', `Text0_0`='¿Qué puedes hacer en una posada?$B$BBueno, cuando te quedas en una posada, descansas cómodamente. Por esta razón, estarás \"más descansado\" en mucho menos tiempo que si lo haces en el bosque. Cuando descansas bien, aprendes más de la experiencia.$B$BAsimismo, puedes pedirle al tabernero que te consiga una piedra de hogar para luego usarla con el fin de regresar rápidamente a la posada.', `Text0_1`='¿Qué puedes hacer en una posada?$B$BBueno, cuando te quedas en una posada, descansas cómodamente. Por esta razón, estarás \"más descansado\" en mucho menos tiempo que si lo haces en el bosque. Cuando descansas bien, aprendes más de la experiencia.$B$BAsimismo, puedes pedirle al tabernero que te consiga una piedra de hogar para luego usarla con el fin de regresar rápidamente a la posada.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1853') AND (`Locale`='esES');
