UPDATE `npc_text_locale` SET `ID`='1933', `Locale`='esES', `Text0_0`='Si la sastrería es lo que te gusta, Vizzklick tiene definitivamente todo lo que necesitas para ello. Echa un vistazo a tu alrededor, $N, y dime si deseas comprar algo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1933') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1935', `Locale`='esES', `Text0_0`='Una luz brillante aparece en la habitación y luego vuelve a desaparecer. Luego adviertes otro rayo... y luego otro.$B$BAl parecer, te quieren mostrar algo utilizando un patrón.', `Text0_1`='Una luz brillante aparece en la habitación y luego vuelve a desaparecer. Luego adviertes otro rayo... y luego otro.$B$BAl parecer, te quieren mostrar algo utilizando un patrón.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1935') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1953', `Locale`='esES', `Text0_0`='La venganza será mía.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1953') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1954', `Locale`='esES', `Text0_0`='La escoria que vive en la Caldera asesinó a mi padre. El Magistrado de Ventormenta hizo oídos sordos a mis súplicas de ayuda. No, no quieren admitir que esta parte del mundo existe y mucho menos enviar a su valiosa milicia para administrar justicia.$B$BBusco venganza, $r, llanamente. ¿Existe una causa más apasionada en este mundo?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1954') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1955', `Locale`='esES', `Text0_0`='Como puedes ver, solo soy un hombre con un escudero medio idiota. El poder de los enanos Hierro Negro y las bestias en manos de Ragnaros seguramente me aplastarían si me encontrasen solo, pero confío plenamente en que un aventurero tan valiente y ambicioso como tú no tendría ningún problema para enfrentarse a los moradores de la caldera. En el caso de que decidieras ayudarme, te recompensaré con las riquezas que jamás hayas podido imaginar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1955') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1973', `Locale`='esES', `Text0_0`='Saludos cordiales te doy, $r. Has venido desde lejanos y polvorientos caminos para simplemente charlar con un morador de esta cueva. Confío en que haya una razón para tu extraña, e imprevista visita aquí. ¿Sí?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1973') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='1994', `Locale`='esES', `Text0_0`='Saludos, $gseñor:señorita;. Soy el aprendiz Nyrill del maestro Xylem. Si tienes algún problema, no dudes en consultar con el maestro.$B$BÉl sin duda vendrá a ayudarte cuando se disponga a regresar a la base en la montaña.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='1994') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2013', `Locale`='esES', `Text0_0`='Interesante, muy interesante... Conozco a una druida que vive en la cumbre de la colina que está encima de las Cuevas de los Lamentos, $N. Quizás pueda decirte algo más sobre este fragmento que has encontrado.$B$B¡Y ahora, he de volver al trabajo!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2013') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2033', `Locale`='esES', `Text0_0`='Los días que hemos pasado aquí en Azshara no son nuestros mejores días ni los más gloriosos, pero aún somos jóvenes y la sangre aún corre por nuestras venas, aquí alcanzaremos la victoria. Solo es cuestión de tiempo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2033') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2036', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Saludos, $N. Veo que has venido desde Orgrimmar para ayudarnos. Me complace. Quiero salir del bosque de los Renegados tan rápido como sea posible. Preferiría pasar el tiempo en Orgrimmar con una buena cerveza en la mano.$B$BSi bien no puedo esperar a oír cómo me cuentas cómo Belgrom te convenció de que vinieras hasta aquí.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2036') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2037', `Locale`='esES', `Text0_0`='¿Qué te trae al Círculo Cenarion, $N?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2037') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2038', `Locale`='esES', `Text0_0`='¡Lo sé, lo sé, estoy aquí en medio de la nada!$B$BMi hogar está en realidad en Trinquete, pero después de que choqué mi zepelín allí, pensé que podía también empezar la investigación que vine a realizar aquí.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2038') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2039', `Locale`='esES', `Text0_0`='El espíritu flota sobre su cuerpo caído. Sus fantasmales ojos te atraviesan mientras mira, absorto, más allá...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2039') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2053', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='La tierra de Feralas ha pasado por muchas cosas, $r. La tierra, los árboles, las criaturas, todos se ven afectados por lo que Feralas es y será.$B$BLos Dos Colosos que están sobre nosotros son una constante, no obstante, todo lo que ha pasado aquí...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2053') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2054', `Locale`='esES', `Text0_0`='Para bajar con seguridad daré alas a quien las necesite.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2054') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2055', `Locale`='esES', `Text0_0`='Bueno, ahora que lo mencionas, sí, vi algo que consideré un poco extraño ayer por la tarde.$B$BPasaba cerca de unas antiguas ruinas trol al sudeste de aquí. Bueno, en realidad no estaba corriendo, estaba saltando... ¡La arena está ardiendo!$B$BEn cualquier caso, iba saltando mientras pasaba junto a las ruinas, cuando descubrí un campamento instalado en ese lugar. En realidad, parece un lugar extraño para montar un campamento, si quieres saber mi opinión. Habría husmeado un poco más por la zona, pero uno de esos buitres grandes y peligrosos empezó a perseguirme.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2055') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2113', `Locale`='esES', `Text0_0`='¡Gracias por salvarme!$B$BSoy cocinero y alquimista de Ventormenta. Vine aquí buscando hierbas de la zona y recetas locales para mi tío Angus cuando me capturaron esos jabaespines. ¡Si no hubieras acudido en mi ayuda, seguramente habría terminado en su olla de potajes!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2113') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2114', `Locale`='esES', `Text0_0`='Um... sí, tengo una receta que te podría ser útil. ¡Deja que te la enseñe!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2114') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2115', `Locale`='esES', `Text0_0`='Bueno, sí, sí sé una receta rara de alquimia. ¡Deja que te la enseñe!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2115') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2133', `Locale`='esES', `Text0_0`='¡Saludos! Si has venido para acceder a los sistemas de ingeniería goblin, prepara tu tarjeta de miembro del Club de Ingeniería Goblin para comprobarla.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2133') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2134', `Locale`='esES', `Text0_0`='¡Saludos! Si has venido para acceder a los sistemas de ingeniería gnómica, prepara tu tarjeta de ingeniero miembro de los gnomos para comprobarla.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2134') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2135', `Locale`='esES', `Text0_0`='Hola, $gmuchacho:muchacha;... mi nombre es Oglethorpe y es posible que sea el creador del aparato más fascinante que nunca hayas tenido en tu poder. Si te ha explotado, no soy responsable. Si por el contrario te ha hecho rico, ¡podemos empezar a hablar de los derechos de autor!$B$BAh, y si eres de Gnomeregan no tengo nada que hablar contigo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2135') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2136', `Locale`='esES', `Text0_0`='¡Pero si es $N! Me alegro de verte otra vez. Apenas puedo expresar mi gratitud por la ayuda que nos has brindado hasta hoy a mí y a mi trabajo de inventor.$B$B¿Qué puedo hacer por ti hoy?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2136') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2137', `Locale`='esES', `Text0_0`='Soy el maestro manitas Sobrechispa y soy el arquitecto jefe encargado de desarrollar los principios actuales de ingeniería gnómica del mundo. Mi trabajo es garantizar que el poder de la invención continúe brillando con mayor intensidad desde nuestras llaves de tuerca arcoluz que desde cualquier otro lugar.$B$BAsimismo, si de casualidad conoces un buen sistema de eliminación de radiaciones, estamos buscando uno en el mercado ahora mismo...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2137') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2138', `Locale`='esES', `Text0_0`='Déjame enseñarte algo de mates: \"goblin\" más \"ingeniería\" igual a SABER. Si alguien te dice otra cosa es que no tiene ni idea de sumar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2138') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2139', `Locale`='esES', `Text0_0`='A través de mis estudios he podido establecer que el hecho de vencer al avatar del dios Hakkar lo ha debilitado. Pero conserva la mayor parte de su fuerza, acechando en el fondo del Averno.$B$BHas vencido a su avatar una vez, $N. Tal vez si lo vuelves a derrotar lograrás debilitar al dios hasta tal punto que, un día, podamos enfrentarnos a su verdadero ser.$B$BHe preparado un encantamiento que puedes usar para volver a invocar un avatar de Hakkar en el Sagrario del Dios Caído. ¿Quieres volver a enfrentarte a él?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2139') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2140', `Locale`='esES', `Text0_0`='Saludos, $c. Aunque el tiempo y la bebida han embotado mis sentidos, mis ojos interiores continúan siendo fuertes.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2140') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2153', `Locale`='esES', `Text0_0`='Hola, $N. Me encargo de entrenar y cuidar a los jóvenes hipogrifos; echa un vistazo tranquilamente.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2153') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2154', `Locale`='esES', `Text0_0`='Los druidas son muy sensibles a las dificultades que padecen todas las criaturas del mundo. Cuando se descubrió que estas aves corrían peligro, ideamos un sistema para rescatarlas de la descomposición de Feralas.$B$BUna gnoma llamada Curgle Saltamanivela se ocupa del asunto. Recoge los huevos y los envía hasta aquí y yo me encargo de cuidarlos.$B$BPodríamos buscar más ayuda para nuestra causa, $N. Tal vez deberías dirigirte a Gadgetzan y buscar a Curgle.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2154') AND (`Locale`='esES');
