UPDATE `npc_text_locale` SET `ID`='2734', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Has visto todas las cosas maravillosas que hay en el cráter de Un\'Goro, $N? Es sencillamente asombroso. No tanto como algunas cosas que podría hacer yo con mis conocimientos de ingeniería, pero para ser todo natural aquí... no está mal.$B$BA mi tío Tripirrem le encanta todo lo antiguo, como este lugar. Yo sigo sus pasos... ¡También quiero conocer cosas nuevas de culturas perdidas!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2734') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='2753', `Locale`='esES', `Text0_0`='Este es el sistema de transporte más moderno y sofisticado que el dinero permite construir. ¿Qué? ¿El Tranvía Subterráneo? ¿Gnomos? Escucha, $gamigo:amiga;, si te crees esa basura, entonces puedo venderte una estatua que hay en Tuercespina. ¿Vas a ir andando hasta Grom\'Gol o vas a montar en esta sofisticada máquina voladora? Tú verás.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='2753') AND (`Locale`='esES');
