UPDATE `npc_text_locale` SET `ID`='3001', `Locale`='esES', `Text0_0`='¿¿Qué querer de Raze??', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3001') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3016', `Locale`='esES', `Text0_0`='De todos los lugares maravillosos de Darnassus, ¿en qué dirección quieres que te oriente?', `Text0_1`='De todos los lugares maravillosos de Darnassus, ¿en qué dirección quieres que te oriente?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3016') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3017', `Locale`='esES', `Text0_0`='Puedes guardar tus bienes más valiosos en el banco de Darnassus, que está en el centro de una ciudad maravillosa.', `Text0_1`='Puedes guardar tus bienes más valiosos en el banco de Darnassus, que está en el centro de una ciudad maravillosa.', `Text1_0`='¡Vaya, otro materialista! Bueno, si estás $gobsesionado:obsesionada; con tus preciosos objetos seguramente querrás guardarlos en el banco de Darnassus, en el centro de nuestra ciudad.', `Text1_1`='¡Vaya, otro materialista! Bueno, si estás $gobsesionado:obsesionada; con tus preciosos objetos seguramente querrás guardarlos en el banco de Darnassus, en el centro de nuestra ciudad.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3017') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3018', `Locale`='esES', `Text0_0`='Los hipogrifos de Vesprystus te conducirán rápidamente a tu destino. Búscalo en la aldea Ruth\'theran, a través del portal que está en la zona oeste de Darnassus.', `Text0_1`='Los hipogrifos de Vesprystus te conducirán rápidamente a tu destino. Búscalo en la aldea Ruth\'theran, a través del portal que está en la zona oeste de Darnassus.', `Text1_0`='Es a Vesprystus a quien buscas. Guarda a los hipogrifos que llegan y parten de la base de Teldrassil. Lo encontrarás atravesando el portal que se encuentra al oeste de Darnassus y que conduce a Aldea Rut\'theran.', `Text1_1`='Es a Vesprystus a quien buscas. Guarda a los hipogrifos que llegan y parten de la base de Teldrassil. Lo encontrarás atravesando el portal que se encuentra al oeste de Darnassus y que conduce a Aldea Rut\'theran.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3018') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3019', `Locale`='esES', `Text0_0`='Si deseas formar una hermandad, tienes que buscar a Lysheana, maestra de hermandades. La encontrarás en el Bancal del Artesano, en la zona noreste de Darnassus.', `Text0_1`='Si deseas formar una hermandad, tienes que buscar a Lysheana, maestra de hermandades. La encontrarás en el Bancal del Artesano, en la zona noreste de Darnassus.', `Text1_0`='Lysheana puede ofrecerte los documentos adecuados, reside en el Bancal del Artesano en la parte noreste de Darnassus. ¡Que tu asociación llene de gloria a los elfos de la noche!', `Text1_1`='Lysheana puede ofrecerte los documentos adecuados, reside en el Bancal del Artesano en la parte noreste de Darnassus. ¡Que tu asociación llene de gloria a los elfos de la noche!', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3019') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3020', `Locale`='esES', `Text0_0`='Si bien para mí no hay mejor cama que la fresca hierba que cubre las praderas de Teldrassil, las camas que Saelienne prepara en la posada del Bancal de los Artesanos son muy adecuadas para los sibaritas.', `Text0_1`='Si bien para mí no hay mejor cama que la fresca hierba que cubre las praderas de Teldrassil, las camas que Saelienne prepara en la posada del Bancal de los Artesanos son muy adecuadas para los sibaritas.', `Text1_0`='¿Ya ha amanecido? ¡La noche nunca tiene suficientes horas! Bueno, Saelienne cuidará bien de ti. Puedes encontrarla atendiendo a sus huéspedes en el Bancal del Artesano, en la parte noreste de Darnassus.', `Text1_1`='¿Ya ha amanecido? ¡La noche nunca tiene suficientes horas! Bueno, Saelienne cuidará bien de ti. Puedes encontrarla atendiendo a sus huéspedes en el Bancal del Artesano, en la parte noreste de Darnassus.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3020') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3021', `Locale`='esES', `Text0_0`='Hay un buzón en la parte exterior del banco. Lo encontrarás en el centro de Darnassus.', `Text0_1`='Hay un buzón en la parte exterior del banco. Lo encontrarás en el centro de Darnassus.', `Text1_0`='¿Un buzón? ¿Tienes que enviar una carta o tal vez has recibido una? ¡Espero que sean buenas noticias! Encontrarás un buzón a las puertas del banco en el centro de Darnassus.', `Text1_1`='¿Un buzón? ¿Tienes que enviar una carta o tal vez has recibido una? ¡Espero que sean buenas noticias! Encontrarás un buzón a las puertas del banco en el centro de Darnassus.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3021') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3022', `Locale`='esES', `Text0_0`='Aquí en Darnassus encontrarás expertos instructores que han perfeccionado las habilidades de su clase elegida en años de entrenamiento y dedicación. Puedo ponerte en contacto con el que podría ser tu mentor. Basta que digas que este es el camino que has elegido.', `Text0_1`='Aquí en Darnassus encontrarás expertos instructores que han perfeccionado las habilidades de su clase elegida en años de entrenamiento y dedicación. Puedo ponerte en contacto con el que podría ser tu mentor. Basta que digas que este es el camino que has elegido.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3022') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3023', `Locale`='esES', `Text0_0`='Eres afortunado al venir a Darnassus, en donde podrás encontrarte con una cazadora tan avezada como Jocaste entre nuestros instructores. Te animo a que la visites en el Enclave Cenarion y que le pidas que sea tu tutora.', `Text0_1`='Eres afortunado al venir a Darnassus, en donde podrás encontrarte con una cazadora tan avezada como Jocaste entre nuestros instructores. Te animo a que la visites en el Enclave Cenarion y que le pidas que sea tu tutora.', `Text1_0`='Jocaste fue la mejor cazadora de su tiempo. Hoy puedes encontrarla en Enclave Cenarion donde se dedica a instruir a los que comienzan a recorrer la senda del cazador.', `Text1_1`='Jocaste fue la mejor cazadora de su tiempo. Hoy puedes encontrarla en Enclave Cenarion donde se dedica a instruir a los que comienzan a recorrer la senda del cazador.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3023') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3024', `Locale`='esES', `Text0_0`='En eterna vigía en su protección del mundo natural, los druidas están guiados por el archidruida Fandral Corzocelada que reside en el Enclave Cenarion. Lo encontrarás junto a los otros instructores de druidas en la sección septentrional de Darnassus.', `Text0_1`='En eterna vigía en su protección del mundo natural, los druidas están guiados por el archidruida Fandral Corzocelada que reside en el Enclave Cenarion. Lo encontrarás junto a los otros instructores de druidas en la sección septentrional de Darnassus.', `Text1_0`='En momentos difíciles, un druida puede ser un cálido amigo a quien acudir. Pregunta a cualquier druida del Enclave Cenarion y te dirán lo mismo.', `Text1_1`='En momentos difíciles, un druida puede ser un cálido amigo a quien acudir. Pregunta a cualquier druida del Enclave Cenarion y te dirán lo mismo.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3024') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3025', `Locale`='esES', `Text0_0`='El Templo de la Luna es el hogar de los sacerdotes de Elune. Allí encontrarás a Jandria y Lariia, quienes te proporcionarán toda la ayuda necesaria en tu camino al sacerdocio.', `Text0_1`='El Templo de la Luna es el hogar de los sacerdotes de Elune. Allí encontrarás a Jandria y Lariia, quienes te proporcionarán toda la ayuda necesaria en tu camino al sacerdocio.', `Text1_0`='¡El poder de Elune te obliga! ¡El poder de Elune te obliga!$B¡Puedes marcharte! ¡Busca a Jandria en el pasillo central del Templo de la Luna en la zona sur de Darnassus!', `Text1_1`='¡El poder de Elune te obliga! ¡El poder de Elune te obliga!$B¡Puedes marcharte! ¡Busca a Jandria en el pasillo central del Templo de la Luna en la zona sur de Darnassus!', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3025') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3026', `Locale`='esES', `Text0_0`='Tienes que acudir a Syurna si quieres seguir el camino de los pícaros. Ella vive en el Enclave Cenarion bajando el camino en espiral en el árbol situado más al oeste.', `Text0_1`='Tienes que acudir a Syurna si quieres seguir el camino de los pícaros. Ella vive en el Enclave Cenarion bajando el camino en espiral en el árbol situado más al oeste.', `Text1_0`='Existen algunos que intentan seguir el camino de los pícaros, pero solo los elfos de la noche disfrutan del cobijo de la noche. Si quieres seguir el camino tenebroso, busca a Syurna en el Enclave Cenarion.', `Text1_1`='Existen algunos que intentan seguir el camino de los pícaros, pero solo los elfos de la noche disfrutan del cobijo de la noche. Si quieres seguir el camino tenebroso, busca a Syurna en el Enclave Cenarion.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3026') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3033', `Locale`='esES', `Text0_0`='Encontrarás a Sildinair en el Bancal del Guerrero entre los guardias, escrutando el bosque con sus ojos despiertos en busca de enemigos que pretendan entrar en nuestra hermosa ciudad.', `Text0_1`='Encontrarás a Sildinair en el Bancal del Guerrero entre los guardias, escrutando el bosque con sus ojos despiertos en busca de enemigos que pretendan entrar en nuestra hermosa ciudad.', `Text1_0`='Sildinair ha instruido a muchos de los centinelas que guardan nuestra amada ciudad. Estoy seguro de que puede distinguir a un guerrero de tu talla. Búscala en el Bancal del Guerrero junto a la entrada de la ciudad.', `Text1_1`='Sildinair ha instruido a muchos de los centinelas que guardan nuestra amada ciudad. Estoy segura de que puede distinguir a un guerrero de tu talla. Búscala en el Bancal del Guerrero junto a la entrada de la ciudad.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3033') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3034', `Locale`='esES', `Text0_0`='Los artesanos de Darnassus encierran un milenio de conocimiento para el beneficio del oficio que has elegido. Ahora dime, ¿qué oficio consideras el más interesante?', `Text0_1`='Los artesanos de Darnassus encierran un milenio de conocimiento para el beneficio del oficio que has elegido. Ahora dime, ¿qué oficio consideras el más interesante?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3034') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3035', `Locale`='esES', `Text0_0`='Si realmente deseas aprender esta profesión, te guiaría hasta Ainethil, una alquimista experta que está en el Bancal del Artesano, en el norte de Darnassus.', `Text0_1`='Si realmente deseas aprender esta profesión, te guiaría hasta Ainethil, una alquimista experta que está en el Bancal del Artesano, en el norte de Darnassus.', `Text1_0`='¿Un alquimista? Ningún problema, $c. Ainethil, gracias a su trabajo duro y esfuerzo, se ha convertido en la mejor alquimista del Bancal del Artesano. Si fuera tú iría allí sin perder tiempo. ¡Sin perder tiempo! ¡Vamos, vete!', `Text1_1`='¿Un alquimista? Ningún problema, $c. Ainethil, gracias a su trabajo duro y esfuerzo, se ha convertido en la mejor alquimista del Bancal del Artesano. Si fuera tú iría allí sin perder tiempo. ¡Sin perder tiempo! ¡Vamos, vete!', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3035') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3036', `Locale`='esES', `Text0_0`='Alegorn es un maestro chef que lleva perfeccionando su cocina desde hace siglos. Deberías considerarte tremendamente afortunado en el caso de que decidiera enseñarte. Si crees que eres $gdigno:digna; de ser su aprendiz, lo encontrarás en el Bancal del Artesano.', `Text0_1`='Alegorn es un maestro chef que lleva perfeccionando su cocina desde hace siglos. Deberías considerarte tremendamente afortunado en el caso de que decidiera enseñarte. Si crees que eres $gdigno:digna; de ser su aprendiz, lo encontrarás en el Bancal del Artesano.', `Text1_0`='Los platos creados por Alegorn son para morirse. Siempre está ocupado preparando nuevas delicias en el Bancal del Artesano. No olvides probar su receta Verde Cenarion, si es que aún le queda algo.$BAhora, si no te importa, tengo que investigar la ola de ciervos desaparecidos.', `Text1_1`='Los platos creados por Alegorn son para morirse. Siempre está ocupado preparando nuevas delicias en el Bancal del Artesano. No olvides probar su receta Verde Cenarion, si es que aún le queda algo.$BAhora, si no te importa, tengo que investigar la ola de ciervos desaparecidos.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3036') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3037', `Locale`='esES', `Text0_0`='Cuando no tienes a nadie que cure tus heridas, los conocimientos de primeros auxilios pueden salvarte la vida. Yo preguntaría a Dannelor, en el Bancal del Artesano, para pedirle instrucción en este oficio.', `Text0_1`='Cuando no tienes a nadie que cure tus heridas, los conocimientos de primeros auxilios pueden salvarte la vida. Yo preguntaría a Dannelor, en el Bancal del Artesano, para pedirle instrucción en este oficio.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3037') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3038', `Locale`='esES', `Text0_0`='Esta habilidad requiere mucho tiempo pero resulta gratificante. Habla con Astaia en el Bancal del Artesano y puede que aprendas algo útil.', `Text0_1`='Esta habilidad requiere mucho tiempo pero resulta gratificante. Habla con Astaia en el Bancal del Artesano y puede que aprendas algo útil.', `Text1_0`='Astaia ha pescado algunos tesoros muy curiosos durante todo el tiempo que ha pasado pescando. ¿Por qué no vas a verla en el Bancal de los Mercaderes y dejas que te enseñe algunos?', `Text1_1`='Astaia ha pescado algunos tesoros muy curiosos durante todo el tiempo que ha pasado pescando. ¿Por qué no vas a verla en el Bancal de los Mercaderes y dejas que te enseñe algunos?', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3038') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3039', `Locale`='esES', `Text0_0`='La recolección y el conocimiento de las hierbas es una especialidad de Firodren Llamaluna. Búscalo en los Jardines del Templo, en la zona sudoeste de Darnassus.', `Text0_1`='La recolección y el conocimiento de las hierbas es una especialidad de Firodren Llamaluna. Búscalo en los Jardines del Templo, en la zona sudoeste de Darnassus.', `Text1_0`='¿Acaso somos herboristas en ciernes? Entonces deberás visitar a Firodren Llamaluna, en los Jardines del Templo. Puede enseñarte algunos trucos del oficio.', `Text1_1`='¿Acaso somos herboristas en ciernes? Entonces deberás visitar a Firodren Llamaluna, en los Jardines del Templo. Puede enseñarte algunos trucos del oficio.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3039') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3040', `Locale`='esES', `Text0_0`='La habilidad con la que Telonis fabrica armaduras no tiene comparación. Deberías agradecer que no te cobre más por el conocimiento que transmite a sus discípulos. Búscalo en el Bancal del Artesano, en la zona noreste de Darnassus.', `Text0_1`='La habilidad con la que Telonis fabrica armaduras no tiene comparación. Deberías agradecer que no te cobre más por el conocimiento que transmite a sus discípulos. Búscalo en el Bancal del Artesano, en la zona noreste de Darnassus.', `Text1_0`='Plantamos las verduras. El conejo se come las verduras. El lobo devora al conejo. Nosotros matamos al lobo. Aprovechamos la carne y el pellejo del lobo. Es el círculo mágico de la vida, o como quieras llamarlo.$BAhora ve a ver a Telonis en el Enclave Cenarion para iniciar tu camino y déjame regresar a mis labores.', `Text1_1`='Plantamos las verduras. El conejo se come las verduras. El lobo devora al conejo. Nosotros matamos al lobo. Aprovechamos la carne y el pellejo del lobo. Es el círculo mágico de la vida, o como quieras llamarlo.$BAhora ve a ver a Telonis en el Enclave Cenarion para iniciar tu camino y déjame regresar a mis labores.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3040') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3042', `Locale`='esES', `Text0_0`='Desde la muerte, los animales nos proporcionan vida y protección. Si crees que puedes mostrar el debido respeto por su sacrificio, puede que la profesión del desollador sea la indicada para ti. Para aprender más sobre este oficio, habla con Eladriel en el Bancal del Artesano, en el norte de Darnassus.', `Text0_1`='Desde la muerte, los animales nos proporcionan vida y protección. Si crees que puedes mostrar el debido respeto por su sacrificio, puede que la profesión del desollador sea la indicada para ti. Para aprender más sobre este oficio, habla con Eladriel en el Bancal del Artesano, en el norte de Darnassus.', `Text1_0`='De acuerdo a nuestra cultura creemos que no puede desperdiciarse nada. Si nos vemos obligados a dar caza a una criatura, hacemos todo lo posible para que su sacrificio no sea en vano.$BSi deseas aprender el modo de quitarle la piel a una bestia a la que se ha dado muerte, busca a Eladriel en el Bancal del Artesano.', `Text1_1`='De acuerdo a nuestra cultura creemos que no puede desperdiciarse nada. Si nos vemos obligados a dar caza a una criatura, hacemos todo lo posible para que su sacrificio no sea en vano.$BSi deseas aprender el modo de quitarle la piel a una bestia a la que se ha dado muerte, busca a Eladriel en el Bancal del Artesano.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3042') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3044', `Locale`='esES', `Text0_0`='Si pretendes convertirte en sastre sin duda tendrás que acudir a Me\'Lynn. Ella vive en el Bancal del Artesano en Darnassus, al nordeste.', `Text0_1`='Si pretendes convertirte en sastre sin duda tendrás que acudir a Me\'Lynn. Ella vive en el Bancal del Artesano en Darnassus, al nordeste.', `Text1_0`='Mirando las prendas elaboradas por Me\'Lynn podría pensarse que su aguja ha sido ungida por la propia Elune. A menos que tu aguja también lo esté, lo mejor será que le hagas una visita y que atiendas a sus enseñanzas. La puedes encontrar en el Bancal del Artesano, en la parte noreste de Darnassus.', `Text1_1`='Mirando las prendas elaboradas por Me\'Lynn podría pensarse que su aguja ha sido ungida por la propia Elune. A menos que tu aguja también lo esté, lo mejor será que le hagas una visita y que atiendas a sus enseñanzas. La puedes encontrar en el Bancal del Artesano, en la parte noreste de Darnassus.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3044') AND (`Locale`='esES');
