UPDATE `npc_text_locale` SET `ID`='3075', `Locale`='esES', `Text0_0`='La casa de subastas de Orgrimmar está en el Valle de la Fuerza. Búscala en el este, junto a Importaciones Bocarda.', `Text0_1`='La casa de subastas de Orgrimmar está en el Valle de la Fuerza. Búscala en el este, junto a Importaciones Bocarda.', `Text1_0`='Tienes la voz ronca de tanto gritar, ¿eh? Encuentra la casa de subastas de Orgrimmar en el Valle de la Fuerza.', `Text1_1`='¿Tienes la voz ronca de tanto gritar, eh? Encuentra la casa de subastas de Orgrimmar en el Valle de la Fuerza.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3075') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3077', `Locale`='esES', `Text0_0`='Son sorprendentes, ¿verdad? Todos estos artefactos y restos.$B$BNo sé qué me impresiona más, si su tamaño o las historias que se cuentan sobre ellos. Hay tanto que estudiar y aprender... y los vínculos que se pueden establecer... $B$B¿Sabías que se dice que muchas razas comparten la herencia de los elfos de la noche?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3077') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3094', `Locale`='esES', `Text0_0`='Por lo más sagrado, dime que has venido a rescatarnos de esta tierra abandonada. Aunque estoy con Expediciones Marshal, te daría mi diente de oro por subirme en tu mochila y dejar este lugar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3094') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3095', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Hola! Soy Hol\'anyee, la jefa científica y bióloga de Expediciones Marshal. Mi hermano y yo, junto con nuestros compañeros, viajamos por Azeroth para explorar la majestuosidad de la naturaleza salvaje. Es emocionante, pero... um... ¡Creo que esta vez nos hemos excedido en nuestra dosis de emoción!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3095') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3096', `Locale`='esES', `Text0_0`='Hola, $gextraño:extraña;, ¡te doy la bienvenida a la majestuosidad salvaje del cráter Un\'Goro!$B$BMi nombre es Williden Marshal. Mi hermana, nuestros compañeros y yo formamos Expediciones Marshal, un grupo de científicos y exploradores del Azeroth más salvaje y desconocido.$B$BHemos tenido un percance serio y necesitamos que nos ayudes si puedes.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3096') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3133', `Locale`='esES', `Text0_0`='No tengo idea de por qué quieres abandonar estas playas soleadas y aguas cristalinas. Esto es como el paraíso. Pero no se lo digas a mi hermano Zapetta. Ya está suficientemente enfadado por tener que pasar el día entero en Tirisfal. Este zepelín hace parada en Orgrimmar, así que no tendrás que escuchar sus lamentos. Pero si vas pronto a Tirisfal, sigue mi consejo y no dejes que se queje.', `Text0_1`='No tengo idea de por qué quieres abandonar estas playas soleadas y aguas cristalinas. Esto es como el paraíso. Pero no se lo digas a mi hermano Zapetta. Ya está suficientemente enfadado por tener que pasar el día entero en Tirisfal. Este zepelín hace parada en Orgrimmar, así que no tendrás que escuchar sus lamentos. Pero si vas pronto a Tirisfal, sigue mi consejo y no dejes que se queje.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3133') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3153', `Locale`='esES', `Text0_0`='Busca a la tabernera Pala en el edificio que hay al sur del Banco de Cima del Trueno, en la pequeña pendiente.', `Text0_1`='Busca a la tabernera Pala en el edificio que hay al sur del Banco de Cima del Trueno, en la pequeña pendiente.', `Text1_0`='La tabernera Pala te ayudará a descansar el cuerpo y el espíritu. Busca a la tabernera Pala en el edificio que hay al sur del Banco de Cima del Trueno, en la pequeña pendiente.', `Text1_1`='La tabernera Pala te ayudará a descansar el cuerpo y el espíritu. Busca a la tabernera Pala en el edificio que hay al sur del Banco de Cima del Trueno, en la pequeña pendiente.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3153') AND (`Locale`='esES');
