UPDATE `npc_text_locale` SET `ID`='3155', `Locale`='esES', `Text0_0`='Está en la zona baja de Cima del Trueno. Busca junto al banco. No está lejos de aquí.', `Text0_1`='Está en la zona baja de Cima del Trueno. Busca junto al banco. No está lejos de aquí.', `Text1_0`='La casa de subastas se encuentra en la cumbre central de Cima del Trueno. Busca junto a la parte inferior de la Torre de los jinetes del viento. Que tus antepasados cuiden de ti.', `Text1_1`='La casa de subastas se encuentra en la cumbre central de Cima del Trueno. Busca junto a la parte inferior de la Torre de los jinetes del viento. Que tus antepasados cuiden de ti.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3155') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3156', `Locale`='esES', `Text0_0`='Puede que esto te escandalice, pero el negocio no ha ido nada bien últimamente...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3156') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3157', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Te doy la bienvenida a Theramore, $N.$B$BTe pido disculpas por ser tan cortante y por no tener tiempo para atenderte como es debido, pero mi ciudad es mi prioridad. Muchas cosas amenazan nuestra forma de vida y la santidad de la Alianza. Debo estar totalmente alerta frente a las fuerzas externas que escapan a nuestro control si quiero que mi gente esté segura y viva feliz.$B$BTal vez tengamos más tiempo para hablar después.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3157') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3158', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Gracias a ti, $N, Hendel está bajo nuestra custodia.$B$BSi bien el interrogatorio es lento, te aseguro que nos dirá todo lo que sabe.$B$BPor ahora, ten paciencia. Te buscaré de inmediato si necesitamos ayuda.$B$BHasta entonces, disfruta de Theramore y te invito a quedarte el tiempo que quieras dentro de los muros de nuestra ciudad como amigo de la Alianza.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3158') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3173', `Locale`='esES', `Text0_0`='Justo fuera de la Entrada Principal de la ciudad hay un poste indicador. Apunta hacia la torre del zepelín. Habla con el maestro de zepelines Frezza, en la parte superior de la torre.', `Text0_1`='Justo fuera de la Entrada Principal de la ciudad hay un poste indicador. Apunta hacia la torre del zepelín. Habla con el maestro de zepelines Frezza, en la parte superior de la torre.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3173') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3193', `Locale`='esES', `Text0_0`='Mi hermano y yo hemos dedicado nuestra vida a Sylvanas. Ella es nuestro faro de luz en los momentos más oscuros. Nos sacrificamos todos los días para garantizar que nuestra especie supere el destino que nos ha elegido.$B$BNo seguiremos siendo débiles. Recuperaremos lo que es nuestro. Devolveremos por diez el dolor que nos han infligido. Y lo haremos sonrientes.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3193') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3213', `Locale`='esES', `Text0_0`='<Volcor no te mira cuando te acercas a él. Parece concentrarse en evitar que se le caigan las vendas. Tras una pausa, levanta la mirada hacia ti, un poco perpleja.>$B$BOh, disculpa, $c. Este no es un buen momento. Espero que lo comprendas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3213') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3214', `Locale`='esES', `Text0_0`='Bueno, tal como lo veo, $N, tenemos dos opciones al abandonar esta caverna.$B$BPodemos irnos a hurtadillas: yo uso la capa que me diste y tú tendrás que buscarte la vida o, y esta puede ser una opción más difícil, considerando mis heridas, luchamos de espaldas, cada cual por su cuenta.$B$BDejo que lo decidas tú, ya que conoces tus fuerzas más que yo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3214') AND (`Locale`='esES');
