UPDATE `npc_text_locale` SET `ID`='3409', `Locale`='esES', `Text0_0`='Si escucho la más mínima palabra sobre aves acuáticas, necesitarás un tubo metido en la garganta para poder comer.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3409') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3412', `Locale`='esES', `Text0_0`='Gruños me llama bobo porque soy demasiado lento. Solo quiero hacer bien mi trabajo, y eso lleva su tiempo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3412') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3415', `Locale`='esES', `Text0_0`='No hago más que escuchar, pero no consigo oír los espíritus de los que habla Karn.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3415') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3418', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Tu interrupción me está costando tiempo, un tiempo muy valioso, más vale que merezca la pena.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3418') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3421', `Locale`='esES', `Text0_0`='Algún día, quiero forjar armas y armaduras con los huesos de mis enemigos.', `Text0_1`='Algún día, quiero forjar armas y armaduras con los huesos de mis enemigos.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3421') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3424', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='E\' ko es la forma prima del poder sobrenatural, y Juju es la esencia concentrada. Utilizando un encantamiento puedo transformar el E\' ko en Juju. Cuando se convierte en Juju puedes utilizar el Alijo de Mau\' ari para reflejar en ti los poderes.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3424') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3456', `Locale`='esES', `Text0_0`='La vida de un herrero no es fácil, pero llena de acero tus huesos y de fuego tu corazón. Mi padre era herrero, y mi abuelo antes que él en las forjas bajo Grim Batol. Y ahora mi hijo sigue nuestros pasos, estoy orgulloso de ese chico.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3456') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3461', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='No salgas así de las sombras. ¡La próxima vez avisa!', `Text1_0`=NULL, `Text1_1`='Desde que las sombras reclamaron el Bosque del Ocaso no he podido volver a dormir. No me dejan dormir los fantasmales sonidos de gritos que trae la brisa. Parecen gritos de corderos, y nunca se detendrán.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3461') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3464', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Haces un buen trabajo, aunque algo desigual en los bordes. No te preocupes, lo superarás con la práctica. Y, hablando de práctica, ¿qué te parece si vemos lo que hemos estado haciendo?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3464') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3466', `Locale`='esES', `Text0_0`='Si le muestro a Saru de lo que soy capaz no tendrá otra opción que dejarme luchar por una de sus hijas. Entonces veremos quién ríe.', `Text0_1`=NULL, `Text1_0`='Aléjate de Sumi y Tumi. Están... comprometidas.', `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3466') AND (`Locale`='esES');
