UPDATE `npc_text_locale` SET `ID`='3488', `Locale`='esES', `Text0_0`='¿Sí? Pues claro que soy un herrero. ¿Qué? ¿Crees que los enanos son los únicos que valen su peso en yunques? Muy bien, pues que así sea. ¡Encuentra a un enano que te ayude!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3488') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3493', `Locale`='esES', `Text0_0`='Aunque odio admitirlo, el oso me superó con creces. ¡Dios mío, es un oso enorme!$B$BNo obstante, si puedes vencer al oso será una hazaña de la que estar orgulloso. Ha dado problemas a muchos de nuestros mejores cazadores.$B$B<Te da unos golpecitos en la espalda.>$B$BDerrota al oso y te aceptaremos como miembro regular de la Cabaña del Errante.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3493') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='3494', `Locale`='esES', `Text0_0`='Aquí, en la Cabaña del Errante valoramos la habilidad cazando más que cualquier otra cosa. ¿Quieres mi aprobación? Tendrás que ganártela.$B$B¡Enfréntate al desafío, $gmuchacho:muchacha;!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='3494') AND (`Locale`='esES');
