UPDATE `npc_text_locale` SET `ID`='4118', `Locale`='esES', `Text0_0`='No ves que estamos tratando de prepararnos para la visita de Uther? ¡Ahora quítate de mi camino, tengo mucho trabajo que hacer!', `Text0_1`=NULL, `Text1_0`='¿Has probado nuestro pan? El Panadero Hijomaestro debe ser el cocinero mejor dotado que haya vivido alguna vez!', `Text1_1`=NULL, `Text2_0`='$GBienvenido:Bienvenida; a Castel Darrow, $gforastero:forastera;.', `Text2_1`=NULL, `Text3_0`='No somos en realidad gente ruda, $gforastero:forastera;, lo que sucede es que tenemos mucha prisa, intentando de que todo esté perfecto para la llegada de Uther. No es frecuente que el Iluminado venga al pueblo.', `Text3_1`=NULL, `Text4_0`='Otro día encantador en nuestra hermosa isla.', `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4118') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='4119', `Locale`='esES', `Text0_0`='Ten cuidado, $gforastero:forastera;. No admitimos a sinvergüenzas en nuestro pueblo.', `Text0_1`=NULL, `Text1_0`='Tengo los ojos puestos en tí, $r.', `Text1_1`=NULL, `Text2_0`='Tengo reprimida mucha ira y hostilidad. Solo dame un motivo, granuja.', `Text2_1`=NULL, `Text3_0`='¡Porque luces como $gun:una; bribonzuel$go:a; callejer$go:a; vulgar!', `Text3_1`=NULL, `Text4_0`='No hay merodeador, mendigo, discusión, pelea o nada que estés pensando hacer, que esté permitido en Castel Darrow.', `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4119') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='4134', `Locale`='esES', `Text0_0`='Bien $ghallado:hallada;, $gamigo:amiga;. Soy el oficial Garush, orgulloso miembro de una organización mundial conocida como el Alba Argenta. Nuestra causa es combatir rápidamente y sin compasión alguna cualquier representación del mal que aparezca en Azeroth. No somos una organización política ni deseamos serlo. Nuestra causa es excepcional y por ella consideramos amigas a la Alianza y a la Horda... ¡amigas por la aniquilación del mal!$B$B¡Por el Alba $ghermano:hermana;!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4134') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='4135', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Saludos, camarada. Soy la oficial Purocorazón, miembro orgulloso de una organización mundial conocida como El Alba Argenta. Nuestra causa es simple: combatir rápidamente y sin compasión alguna cualquier representación del mal que aparezca en Azeroth. No somos una organización política ni deseamos serlo. Con ese fin consideramos amigas a la Alianza y a la Horda... ¡amigas por la aniquilación del mal!$B$B¡Por El Alba!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4135') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='4173', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='El Alba Argenta te da la bienvenida, $c. Soy Hasana, uno de los pocos intendentes de material y equipamiento autorizados del Alba.$B$BCuando combatas al mal a nuestro servicio (ganando nuestro respeto y confianza) ganarás muestras de valor del oficial Garush. Las muestras tienen dos fines: las puedes utilizar para ganar prestigio entre nuestras filas o como moneda para adquirir objetos especiales.$B$BA medida que ganes prestigio en el Alba verás que cada vez te ofreceremos más objetos.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4173') AND (`Locale`='esES');
