UPDATE `npc_text_locale` SET `ID`='4233', `Locale`='esES', `Text0_0`='Saludos, $c.$B$BEsos centinelas me han permitido compartir su campamento mientras no haya ningún tipo de problemas... Estoy seguro de que podrás darte cuenta de cuánta diplomacia es necesaria por aquí, ¿verdad?$B$BMe dedico a investigar los disturbios del país, y como esa labor ayuda a su propia causa, han acordado que mi presencia no puede sino contribuir a la débil alianza que une a todas las razas de Azeroth. Si quieres ayudarme lo apreciaría pero, aunque decidas no hacerlo, procura no causar ningún problema.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4233') AND (`Locale`='esES');
