UPDATE `npc_text_locale` SET `ID`='4261', `Locale`='esES', `Text0_0`='Si necesitas volar a algún sitio, encontrarás un maestro de grifos en Ventormenta. Deberías poder ver los grifos aterrizando a tu derecha cuando atravieses el Valle de los Héroes.', `Text0_1`='Si necesitas volar a algún sitio, encontrarás un maestro de grifos en Ventormenta. Deberías poder ver los grifos aterrizando a tu derecha cuando atravieses el Valle de los Héroes.', `Text1_0`='El maestro de grifos supervisa los vuelos de grifos que parten desde Ventormenta. Si deseas montar uno de ellos, deberías dirigirte allí.', `Text1_1`='El maestro de grifos supervisa los vuelos de grifos que parten desde Ventormenta. Si deseas montar uno de ellos, deberías dirigirte allí.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4261') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='4262', `Locale`='esES', `Text0_0`='Creo que Aldwin Risante sigue siendo el maestro de hermandad residente de Ventormenta. Encontrarás su residencia a tu izquierda cuando atravieses la puerta interior de Ventormenta.', `Text0_1`='Creo que Aldwin Risante sigue siendo el maestro de hermandad residente de Ventormenta. Encontrarás su residencia a tu izquierda cuando atravieses la puerta interior de Ventormenta.', `Text1_0`='Hoy en día, todos los asuntos oficiales se resuelven en Ventormenta. Puedes hacer un alto en el Centro de Viajeros de Ventormenta si quieres fundar una hermandad.', `Text1_1`='Hoy en día, todos los asuntos oficiales se resuelven en Ventormenta. Puedes hacer un alto en el Centro de Viajeros de Ventormenta si quieres fundar una hermandad.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4262') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='4263', `Locale`='esES', `Text0_0`='No sigas buscando, dirígete a la Posada Orgullo de León de Villadorada. Una amplia selección de cervezas y cómodas camas... no podrías pedir un lugar mejor para descansar.', `Text0_1`='No sigas buscando, dirígete a la Posada Orgullo de León de Villadorada. Una amplia selección de cervezas y cómodas camas... no podrías pedir un lugar mejor para descansar.', `Text1_0`='La Posada Orgullo de León de Villadorada es la mejor que podrás encontrar por estos lares. Entra y deja que el tabernero Farley te haga sentir como en casa.', `Text1_1`='La Posada Orgullo de León de Villadorada es la mejor que podrás encontrar por estos lares. Entra y deja que el tabernero Farley te haga sentir como en casa.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4263') AND (`Locale`='esES');
