UPDATE `npc_text_locale` SET `ID`='4267', `Locale`='esES', `Text0_0`='Josetta ha sido una auténtica bendición para esta ciudad, con la ayuda que ha prestado a la guardia local. Si deseas que te instruya por la senda del sacerdocio la podrás encontrar en la Posada Orgullo de León.', `Text0_1`='Josetta ha sido una auténtica bendición para esta ciudad, con la ayuda que ha prestado a la guardia local. Si deseas que te instruya por la senda del sacerdocio la podrás encontrar en la Posada Orgullo de León.', `Text1_0`='Apuesto que Josetta estaría encantada de enseñarte algunos hechizos. Seguramente la encontrarás en su habitación de la Posada Orgullo de León.', `Text1_1`='Apuesto que Josetta estaría encantada de enseñarte algunos hechizos. Seguramente la encontrarás en su habitación de la Posada Orgullo de León.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4267') AND (`Locale`='esES');
