UPDATE `npc_text_locale` SET `ID`='4269', `Locale`='esES', `Text0_0`='Normalmente puedes encontrar al hermano Wilhelm en la parte de atrás de la herraría, contándose historias con Lyria. Siempre está dispuesto a enseñar nuevas técnicas a los jóvenes paladines.', `Text0_1`='Normalmente puedes encontrar al hermano Wilhelm en la parte de atrás de la herraría, contándose historias con Lyria. Siempre está dispuesto a enseñar nuevas técnicas a los jóvenes paladines.', `Text1_0`='El hermano Wilhelm suele entrenar con Lyria Du Lac en la parte trasera de la herrería de Villadorada. De su mano podrás aprender nuevas cosas sobre la disciplina de un paladín.', `Text1_1`='El hermano Wilhelm suele entrenar con Lyria Du Lac en la parte trasera de la herrería de Villadorada. De su mano podrás aprender nuevas cosas sobre la disciplina de un paladín.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4269') AND (`Locale`='esES');
