UPDATE `npc_text_locale` SET `ID`='4274', `Locale`='esES', `Text0_0`='El ermitaño Mallory prepara de vez en cuando algunas pociones para la población de la ciudad. Tiene una pequeña casa en las colinas entre Ventormenta y Villanorte.', `Text0_1`='El ermitaño Mallory prepara de vez en cuando algunas pociones para la población de la ciudad. Tiene una pequeña casa en las colinas entre Ventormenta y Villanorte.', `Text1_0`='Hay un alquimista que vive en las colinas entre Villanorte y Ventormenta, seguramente podría ayudarte. ¿Por qué no vas a hacerle una visita?', `Text1_1`='Hay un alquimista que vive en las colinas entre Villanorte y Ventormenta, seguramente podría ayudarte. ¿Por qué no vas a hacerle una visita?', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4274') AND (`Locale`='esES');
