UPDATE `npc_text_locale` SET `ID`='4291', `Locale`='esES', `Text0_0`='Los Cebatruenos tienen una agradable posada en Kharanos que lleva su nombre. Entra a tomar una pinta y a descansar un poco.', `Text0_1`='Los Cebatruenos tienen una agradable posada en Kharanos que lleva su nombre. Entra a tomar una pinta y a descansar un poco.', `Text1_0`='Oooh, ¿una posada? No hay ninguna como la Destilería Cebatruenos. Descansa si lo necesitas y tómate una cerveza o dos cuando estés ahí.', `Text1_1`='Oooh, ¿una posada? No hay ninguna como la Destilería Cebatruenos. Descansa si lo necesitas y tómate una cerveza o dos cuando estés ahí.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4291') AND (`Locale`='esES');
