UPDATE `npc_text_locale` SET `ID`='4327', `Locale`='esES', `Text0_0`='Kyra Espada del Viento maneja la espada con gran habilidad. Seguro que puede ofrecerte instrucción. La encontrarás en la posada de Dolanaar.', `Text0_1`='Kyra Espada del Viento maneja la espada con gran habilidad. Seguro que puede ofrecerte instrucción. La encontrarás en la posada de Dolanaar.', `Text1_0`='En una ocasión tuve el placer de ver a Kyra Espada del Viento en combate. Teniendo en cuenta lo que vi, estoy seguro de que te entrenará bien. Búscala en la posada de Dolanaar.', `Text1_1`='Una vez tuve el placer de ver a Kyra Espada del Viento en combate. Con todo de lo que fui testigo, puedo asegurarte que recibirás una buena instrucción. Búscala en la posada de Dolanaar.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4327') AND (`Locale`='esES');
