UPDATE `npc_text_locale` SET `ID`='4329', `Locale`='esES', `Text0_0`='Aunque Cyndra no es tan burbujeante como sus pociones, su amabilidad es idónea para aprender su oficio. Podrás encontrarla al este de la posada de Dolanaar.', `Text0_1`=NULL, `Text1_0`='El conocimiento que Cyndra tiene de la alquimia es algo limitado, pero seguro que será suficiente para que te inicies en la profesión. Ella la ejerce al este de la posada de Dolanaar.', `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4329') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='4330', `Locale`='esES', `Text0_0`='Zarrin parece conocer bien el arte de la cocina. ¿Por qué no vas a ver si te enseña alguna de sus recetas? Normalmente está cocinando junto a la posada de Dolanaar.', `Text0_1`=NULL, `Text1_0`='La verdad es que podríamos necesitar otro cocinero en Dolanaar, aunque no vayas a pensar que a la cocina de Zarrin le falta algo. ¿Por qué no vas a ver qué puede enseñarte?', `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4330') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='4331', `Locale`='esES', `Text0_0`='Alanna Cuervojo atribuye parte de su talento en el arte del encantamiento a su comunión con los espíritus de El Claro del Oráculo. Deberías poder encontrarla allí.', `Text0_1`=NULL, `Text1_0`='Alanna pasa casi todo el día en El Claro del Oráculo, donde ejerce su profesión. Si deseas aprender encantamiento tendrás que ir allí para encontrarla.', `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4331') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='4332', `Locale`='esES', `Text0_0`='Byancie ha prestado a menudo sus servicios a los centinelas que patrullan nuestro entorno natural. Si tu deseo es aprender primeros auxilios deberías ir a hablar con ella.', `Text0_1`=NULL, `Text1_0`='Las aptitudes de Byancie en primeros auxilios se han mostrado útiles en numerosas ocasiones. Si deseas aprender de ella podrás encontrarla al norte de la posada de Dolanaar.', `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4332') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='4333', `Locale`='esES', `Text0_0`='Debido a los recientes ataques en las proximidades del Lago Al\'Ameth, todos los pescadores se han marchado a Darnassus. Deberías buscar a alguien allí que te instruya.', `Text0_1`=NULL, `Text1_0`='Los Brezomadera han hecho que sea muy difícil pescar en los alrededores de Dolanaar. Lo mejor será que busques a alguien que te instruya en Darnassus.', `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4333') AND (`Locale`='esES');
