UPDATE `npc_text_locale` SET `ID`='4335', `Locale`='esES', `Text0_0`='Los Tejecrín ejercen sus profesiones en su hogar, en el camino hacia Darnassus. Nadyia es una excelente instructora de peletería. Deberías ir a ver si puede instruirte.', `Text0_1`='Los Tejecrín ejercen sus profesiones en su hogar, en el camino hacia Darnassus. Nadyia es una excelente instructora de peletería. Deberías ir a ver si puede instruirte.', `Text1_0`='Nadyia se ha convertido en toda una peletera en todos sus años de práctica. Viaja hacia el Oeste siguiendo el camino hacia Darnassus y hazle una visita. quizás pueda transmitirte algo de su conocimiento.', `Text1_1`='Nadyia se ha convertido en toda una peletera en todos sus años de práctica. Viaja hacia el Oeste siguiendo el camino hacia Darnassus y hazle una visita. Quizás pueda transmitirte algo de su conocimiento.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4335') AND (`Locale`='esES');
