UPDATE `npc_text_locale` SET `ID`='4445', `Locale`='esES', `Text0_0`='¡Silencio! ¿No puedes ver que está hablando el maestro?', `Text0_1`='¡Silencio! ¿No puedes ver que está hablando el maestro?', `Text1_0`='Dedica menos tiempo a hablar y más a escuchar.', `Text1_1`='Dedica menos tiempo a hablar y más a escuchar.', `Text2_0`='¡Debemos centrarnos si queremos que desove nuestro Vuelo!', `Text2_1`='¡Debemos centrarnos si queremos que desove nuestro Vuelo!', `Text3_0`='¡Silencio! ¡No puedo permitirme perder ni una sola de sus palabras!', `Text3_1`='¡Silencio! ¡No puedo permitirme perder ni una sola de sus palabras!', `Text4_0`='¿Has olvidado tu uniforme de alumno? ¿Crees que la moda es más importante que la dominación de la Plaga?', `Text4_1`='¿Has olvidado tu uniforme de alumno? ¿Crees que la moda es más importante que la dominación de la Plaga?', `Text5_0`='¿Terminaste las tareas de ayer? ¡Si es así, siéntate a mi lado!', `Text5_1`='¿Terminaste las tareas de ayer? ¡Si es así, siéntate a mi lado!', `Text6_0`='Vigila tus pasos aquí, $gchico:chica;. El maestro lo ve todo...', `Text6_1`='Vigila tus pasos aquí, $gchico:chica;. El maestro lo ve todo...', `Text7_0`='¿Has traído tus libros de texto? ¿Puedo verlos contigo?', `Text7_1`='¿Has traído tus libros de texto? ¿Puedo verlos contigo?' WHERE (`ID`='4445') AND (`Locale`='esES');
