UPDATE `npc_text_locale` SET `ID`='4447', `Locale`='esES', `Text0_0`='Tu mente no está centrada. ¿Me estás ocultando algo?', `Text0_1`=NULL, `Text1_0`='Siento que algo te perturba...', `Text1_1`=NULL, `Text2_0`='Tu futuro está nublado. Concéntrate y pregunta de nuevo.', `Text2_1`=NULL, `Text3_0`='No pareces un estudiante...', `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4447') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='4449', `Locale`='esES', `Text0_0`='¡Este kodo no se parece en nada a la bestia a la que atraje! Me pregunto si podría utilizar el combobulador sobre mí mismo.', `Text0_1`='¡Este kodo no se parece en nada a la bestia a la que atraje! Me pregunto si podría utilizar el combobulador sobre mí misma.', `Text1_0`='¿Por qué esas pequeñas criaturas como los goblins siempre están fascinadas por las criaturas grandes?', `Text1_1`='¿Por qué esas pequeñas criaturas como los goblins siempre están fascinadas por las criaturas grandes?', `Text2_0`='¿Cómo se las arreglan los goblins para asegurar la carga en estas bestias? ¡Los kodos son diez veces más grandes que ellos!', `Text2_1`='¿Cómo se las arreglan los goblins para asegurar la carga en estas bestias? ¡Los kodos son diez veces más grandes que ellos!', `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4449') AND (`Locale`='esES');
