UPDATE `npc_text_locale` SET `ID`='4774', `Locale`='esES', `Text0_0`='Procura mantener el equilibrio de todas las cosas, $c. En él descansa la verdadera armonía.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4774') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='4775', `Locale`='esES', `Text0_0`='$GBienvenido hermano:Bienvenida hermana;. Si has venido para instruirte, será un placer enseñarte.$B$BNunca viene mal reafirmar el lugar propio en lo que me gusta denominar El Gran Equilibrio. Como guardianes de ese equilibrio debemos procurar mantener siempre nuestras mentes limpias y nuestros corazones fuertes. La instrucción nos ayuda a realizar este fin.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4775') AND (`Locale`='esES');
