UPDATE `npc_text_locale` SET `ID`='4779', `Locale`='esES', `Text0_0`='$GBienvenido:Bienvenida;, $ghermano:hermana;. Si has venido en busca de instrucción, estaré encantado de ayudarte.$B$BSomos los protectores del equilibrio, ahora y siempre, mientras Malfurion siga sumido en el Sueño. Nunca lo olvides.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='4779') AND (`Locale`='esES');
