UPDATE `npc_text_locale` SET `ID`='5260', `Locale`='esES', `Text0_0`='Ya sé mucho sobre estilo. Pero ahora se trata de dominar las técnicas del trabajo con cuero. Es estupendo que Simón sea tan comprensivo ayudándome a aprenderlas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5260') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5265', `Locale`='esES', `Text0_0`='Tienes que hacer los puntos más pequeños, $N. Algunos objetos se saldrían de los carcajes y bolsas de municiones. Además, a nadie le gusta una túnica llena de agujeros.', `Text0_1`='Tienes que hacer los puntos más pequeños, $N. Algunos objetos se saldrían de los carcajes y bolsas de municiones. Además, a nadie le gusta una túnica llena de agujeros.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5265') AND (`Locale`='esES');
